            image-rendering: crisp-edges; /* ‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö‡πÉ‡∏ô‡∏≠‡∏ô‡∏≤‡∏Ñ‡∏ï */
        }
    }
    </style>
    <?php
}
add_action('wp_head', 'site_logo_responsive_styles');

/**
 * ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡πÇ‡∏•‡πÇ‡∏Å‡πâ
 */
function save_site_logo($attachment_id, $background_type = 'white') {
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ñ‡∏ß‡∏≤‡∏°‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á‡∏Ç‡∏≠‡∏á attachment_id
    if (!$attachment_id || !wp_attachment_is_image($attachment_id)) {
        return false;
    }
    
    // ‡∏Å‡∏≥‡∏´‡∏ô‡∏î theme_mod ‡∏ï‡∏≤‡∏°‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏û‡∏∑‡πâ‡∏ô‡∏´‡∏•‡∏±‡∏á
    $mod_name = '';
    switch ($background_type) {
        case 'white':
            $mod_name = 'custom_logo';
            break;
        case 'dark':
            $mod_name = 'custom_logo_dark';
            break;
        case 'transparent':
            $mod_name = 'custom_logo_transparent';
            break;
        default:
            $mod_name = 'custom_logo';
    }
    
    // ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å theme mod
    set_theme_mod($mod_name, $attachment_id);
    
    return $attachment_id;
}

/**
 * ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô Helper ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÄ‡∏£‡∏µ‡∏¢‡∏Å‡πÉ‡∏ä‡πâ‡πÇ‡∏•‡πÇ‡∏Å‡πâ‡πÉ‡∏ô‡πÑ‡∏ü‡∏•‡πå‡∏ò‡∏µ‡∏°
 * 
 * ‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô: <?php echo get_site_logo('dark'); ?>
 */
function get_site_logo($mode = 'light', $args = array()) {
    $defaults = array(
        'mode' => $mode,
        'class' => '',
        'size' => 'full',
        'link' => 'yes',
        'alt' => '',
        'width' => '',
        'height' => '',
    );
    
    $args = wp_parse_args($args, $defaults);
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á shortcode attributes string
    $shortcode_atts = '';
    foreach ($args as $key => $value) {
        if (!empty($value)) {
            $shortcode_atts .= ' ' . $key . '="' . esc_attr($value) . '"';
        }
    }
    
    return do_shortcode('[sitelogo' . $shortcode_atts . ']');
}

/**
 * ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô Helper ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÅ‡∏™‡∏î‡∏á‡πÇ‡∏•‡πÇ‡∏Å‡πâ‡πÉ‡∏ô‡πÑ‡∏ü‡∏•‡πå‡∏ò‡∏µ‡∏°
 * 
 * ‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô: <?php site_logo('dark'); ?>
 */
function site_logo($mode = 'light', $args = array()) {
    echo get_site_logo($mode, $args);
}

/**
 * ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÅ‡∏™‡∏î‡∏á‡πÇ‡∏•‡πÇ‡∏Å‡πâ‡∏ï‡∏≤‡∏°‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏û‡∏∑‡πâ‡∏ô‡∏´‡∏•‡∏±‡∏á
 * ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏ô‡∏µ‡πâ‡∏à‡∏∞‡πÉ‡∏ä‡πâ‡∏Å‡∏±‡∏ö auto-detection ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÅ‡∏™‡∏î‡∏á‡πÇ‡∏•‡πÇ‡∏Å‡πâ‡∏ó‡∏µ‡πà‡πÄ‡∏´‡∏°‡∏≤‡∏∞‡∏™‡∏°‡∏Å‡∏±‡∏ö‡∏û‡∏∑‡πâ‡∏ô‡∏´‡∏•‡∏±‡∏á
 */
function get_logo_for_background($background_type = 'auto') {
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏à‡∏∞‡πÉ‡∏ä‡πâ‡πÇ‡∏•‡πÇ‡∏Å‡πâ‡πÅ‡∏ö‡∏ö‡πÑ‡∏´‡∏ô
    if ($background_type === 'auto') {
        // ‡∏ï‡∏£‡∏á‡∏ô‡∏µ‡πâ‡∏ï‡πâ‡∏≠‡∏á‡∏°‡∏µ‡∏Å‡∏≤‡∏£‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏™‡∏µ‡∏û‡∏∑‡πâ‡∏ô‡∏´‡∏•‡∏±‡∏á‡∏î‡πâ‡∏ß‡∏¢ JavaScript
        echo '<script>
            document.addEventListener("DOMContentLoaded", function() {
                // ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏ô‡∏µ‡πâ‡∏à‡∏∞‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏Ñ‡∏ß‡∏£‡πÉ‡∏ä‡πâ‡πÇ‡∏•‡πÇ‡∏Å‡πâ‡πÅ‡∏ö‡∏ö‡πÑ‡∏´‡∏ô
                function checkBackgroundType() {
                    const element = document.querySelector(".site-header"); // ‡∏õ‡∏£‡∏±‡∏ö‡∏ï‡∏≤‡∏°‡∏Ñ‡∏•‡∏≤‡∏™‡∏ó‡∏µ‡πà‡πÉ‡∏ä‡πâ‡∏à‡∏£‡∏¥‡∏á
                    if (!element) return;
                    
                    // ‡∏î‡∏∂‡∏á‡∏™‡∏µ background ‡∏Ç‡∏≠‡∏á header
                    const bgColor = window.getComputedStyle(element).backgroundColor;
                    
                    // ‡πÅ‡∏õ‡∏•‡∏á‡πÄ‡∏õ‡πá‡∏ô‡∏Ñ‡πà‡∏≤ RGB ‡∏´‡∏£‡∏∑‡∏≠ ‡πÅ‡∏õ‡∏•‡∏á‡πÄ‡∏õ‡πá‡∏ô‡∏Ñ‡πà‡∏≤‡∏Ñ‡∏ß‡∏≤‡∏°‡∏™‡∏ß‡πà‡∏≤‡∏á
                    const match = bgColor.match(/rgba?\((\d+), (\d+), (\d+)/);
                    if (match) {
                        const r = parseInt(match[1]);
                        const g = parseInt(match[2]);
                        const b = parseInt(match[3]);
                        
                        // ‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡∏Ñ‡∏ß‡∏≤‡∏°‡∏™‡∏ß‡πà‡∏≤‡∏á (‡∏Ñ‡πà‡∏≤‡πÄ‡∏â‡∏•‡∏µ‡πà‡∏¢)
                        const brightness = (r + g + b) / 3;
                        
                        const logo = document.querySelector(".custom-logo");
                        if (!logo) return;
                        
                        // ‡∏ñ‡πâ‡∏≤‡∏û‡∏∑‡πâ‡∏ô‡∏´‡∏•‡∏±‡∏á‡∏°‡∏∑‡∏î ‡πÉ‡∏ä‡πâ‡πÇ‡∏•‡πÇ‡∏Å‡πâ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏û‡∏∑‡πâ‡∏ô‡∏´‡∏•‡∏±‡∏á‡∏™‡∏µ‡πÄ‡∏Ç‡πâ‡∏°
                        if (brightness < 128) {
                            const lightLogoId = "' . get_theme_mod("custom_logo_dark") . '";
                            if (lightLogoId) {
                                logo.src = "' . wp_get_attachment_url(get_theme_mod("custom_logo_dark")) . '";
                            }
                        }
                    }
                }
                
                // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ï‡∏≠‡∏ô‡πÇ‡∏´‡∏•‡∏î‡πÄ‡∏û‡∏à‡πÅ‡∏•‡∏∞‡πÄ‡∏°‡∏∑‡πà‡∏≠‡∏Ç‡∏ô‡∏≤‡∏î‡∏´‡∏ô‡πâ‡∏≤‡∏à‡∏≠‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô
                checkBackgroundType();
                window.addEventListener("resize", checkBackgroundType);
            });
        </script>';
        
        // ‡πÉ‡∏ä‡πâ microdata ‡∏ó‡∏µ‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á - ‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‡∏à‡∏≤‡∏Å‡πÄ‡∏î‡∏¥‡∏°
        echo '<div itemscope itemtype="https://schema.org/Organization">';
        echo get_custom_logo();
        echo '</div>';
        
        return;
    } else {
        // ‡πÉ‡∏ä‡πâ‡πÇ‡∏•‡πÇ‡∏Å‡πâ‡∏ï‡∏≤‡∏°‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏ó‡∏µ‡πà‡∏£‡∏∞‡∏ö‡∏∏‡πÇ‡∏î‡∏¢‡∏ï‡∏£‡∏á
        if ($background_type === 'dark') {
            return get_site_logo('light'); // ‡∏û‡∏∑‡πâ‡∏ô‡∏´‡∏•‡∏±‡∏á‡πÄ‡∏Ç‡πâ‡∏° ‡πÉ‡∏ä‡πâ mode="light"
        } elseif ($background_type === 'transparent') {
            return get_site_logo('transparent'); // ‡∏û‡∏∑‡πâ‡∏ô‡∏´‡∏•‡∏±‡∏á‡πÇ‡∏õ‡∏£‡πà‡∏á‡πÉ‡∏™ ‡πÉ‡∏ä‡πâ mode="transparent"
        } else {
            return get_site_logo('dark'); // ‡∏û‡∏∑‡πâ‡∏ô‡∏´‡∏•‡∏±‡∏á‡∏™‡∏ß‡πà‡∏≤‡∏á ‡πÉ‡∏ä‡πâ mode="dark"
        }
    }
}

/**
 * ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏ä‡πà‡∏ß‡∏¢‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡πÅ‡∏•‡∏∞‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Ñ‡πà‡∏≤ theme mods ‡πÉ‡∏´‡πâ‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á
 * ‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡πÄ‡∏£‡∏µ‡∏¢‡∏Å‡πÉ‡∏ä‡πâ‡∏à‡∏≤‡∏Å functions.php ‡∏´‡∏£‡∏∑‡∏≠‡∏´‡∏ô‡πâ‡∏≤‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤‡∏ò‡∏µ‡∏°
 */
function fix_site_logo_theme_mods() {
    // ‡∏î‡∏∂‡∏á‡∏Ñ‡πà‡∏≤ theme mods ‡∏õ‡∏±‡∏à‡∏à‡∏∏‡∏ö‡∏±‡∏ô
    $white_logo_id = get_theme_mod('custom_logo');
    $dark_logo_id = get_theme_mod('custom_logo_dark');
    $transparent_logo_id = get_theme_mod('custom_logo_transparent');
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ñ‡πà‡∏≤‡πÅ‡∏•‡∏∞‡πÅ‡∏™‡∏î‡∏á‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞‡∏õ‡∏±‡∏à‡∏à‡∏∏‡∏ö‡∏±‡∏ô
    $status = array(
        'white_logo' => $white_logo_id ? 'OK' : 'Missing',
        'dark_logo' => $dark_logo_id ? 'OK' : 'Missing',
        'transparent_logo' => $transparent_logo_id ? 'OK' : 'Missing'
    );
    
    return $status;
}

/**
 * ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏£‡∏µ‡πÄ‡∏ã‡πá‡∏ï‡∏´‡∏£‡∏∑‡∏≠‡∏•‡πâ‡∏≤‡∏á‡∏Ñ‡πà‡∏≤ theme mods ‡∏ó‡∏µ‡πà‡πÄ‡∏Å‡∏µ‡πà‡∏¢‡∏ß‡∏Ç‡πâ‡∏≠‡∏á‡∏Å‡∏±‡∏ö‡πÇ‡∏•‡πÇ‡∏Å‡πâ
 * ‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡πÄ‡∏£‡∏µ‡∏¢‡∏Å‡πÉ‡∏ä‡πâ‡πÉ‡∏ô‡∏Å‡∏£‡∏ì‡∏µ‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô‡πÉ‡∏´‡∏°‡πà
 */
function reset_site_logo_theme_mods() {
    remove_theme_mod('custom_logo');
    remove_theme_mod('custom_logo_dark');
    remove_theme_mod('custom_logo_transparent');
    
    return array(
        DGA_SUCCESS_STATUS => true,
        DGA_MESSAGE_KEY => '‡∏£‡∏µ‡πÄ‡∏ã‡πá‡∏ï‡∏Ñ‡πà‡∏≤ theme mods ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÇ‡∏•‡πÇ‡∏Å‡πâ‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢‡πÅ‡∏•‡πâ‡∏ß'
    );
}

/**
 * ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏Å‡∏≤‡∏£‡∏™‡∏ô‡∏±‡∏ö‡∏™‡∏ô‡∏∏‡∏ô custom logo ‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î
 * ‡πÉ‡∏´‡πâ‡πÄ‡∏£‡∏µ‡∏¢‡∏Å‡πÉ‡∏ä‡πâ‡πÉ‡∏ô functions.php ‡∏Ç‡∏≠‡∏á‡∏ò‡∏µ‡∏°
