/**
 * AJAX handler ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡πÇ‡∏•‡πÇ‡∏Å‡πâ
 */
function site_logo_update_ajax_handler() {
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö nonce
    if (!isset($_POST['nonce']) || !wp_verify_nonce($_POST['nonce'], 'site_logo_update_nonce')) {
        wp_send_json_error('‡∏Å‡∏≤‡∏£‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ñ‡∏ß‡∏≤‡∏°‡∏õ‡∏•‡∏≠‡∏î‡∏†‡∏±‡∏¢‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß');
        exit;
    }
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö attachment_id
    $attachment_id = isset($_POST['attachment_id']) ? intval($_POST['attachment_id']) : 0;
    if ($attachment_id <= 0) {
        wp_send_json_error('‡πÑ‡∏°‡πà‡∏û‡∏ö‡πÑ‡∏ü‡∏•‡πå‡πÅ‡∏ô‡∏ö‡∏ó‡∏µ‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á');
        exit;
    }
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡πÄ‡∏õ‡πá‡∏ô‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û‡∏à‡∏£‡∏¥‡∏á
    if (!wp_attachment_is_image($attachment_id)) {
        wp_send_json_error('‡πÑ‡∏ü‡∏•‡πå‡πÅ‡∏ô‡∏ö‡πÑ‡∏°‡πà‡πÉ‡∏ä‡πà‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û');
        exit;
    }
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö background_type
    $background_type = isset($_POST['background_type']) ? sanitize_text_field($_POST['background_type']) : 'white';
    if (!in_array($background_type, array('white', 'dark', 'transparent'))) {
        $background_type = 'white';
    }
    
    // ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡πÇ‡∏•‡πÇ‡∏Å‡πâ
    $saved_attachment_id = save_site_logo($attachment_id, $background_type);
    if (!$saved_attachment_id) {
        wp_send_json_error('‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡πÇ‡∏•‡πÇ‡∏Å‡πâ‡πÑ‡∏î‡πâ');
        exit;
    }
    
    // ‡∏ä‡∏∑‡πà‡∏≠‡∏û‡∏∑‡πâ‡∏ô‡∏´‡∏•‡∏±‡∏á‡πÅ‡∏•‡∏∞ shortcode mode
    $background_name = '';
    $shortcode_mode = '';
    switch ($background_type) {
        case 'white':
            $background_name = '‡∏Ç‡∏≤‡∏ß';
            $shortcode_mode = 'dark';
            break;
        case 'dark':
            $background_name = '‡∏™‡∏µ‡πÄ‡∏Ç‡πâ‡∏°';
            $shortcode_mode = 'light';
            break;
        case 'transparent':
            $background_name = '‡πÇ‡∏õ‡∏£‡πà‡∏á‡πÉ‡∏™';
            $shortcode_mode = 'transparent';
            break;
        default:
            $background_name = '‡∏Ç‡∏≤‡∏ß';
            $shortcode_mode = 'dark';
            break;
    }
    
    // ‡∏™‡πà‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏Å‡∏•‡∏±‡∏ö
    wp_send_json_success(array(
        DGA_MESSAGE_KEY => '‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡πÇ‡∏•‡πÇ‡∏Å‡πâ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏û‡∏∑‡πâ‡∏ô‡∏´‡∏•‡∏±‡∏á' . $background_name . '‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢‡πÅ‡∏•‡πâ‡∏ß',
        'shortcode' => '[sitelogo mode="' . $shortcode_mode . '"]',
        'logo_html' => wp_get_attachment_image($saved_attachment_id, 'full', false, array('class' => 'custom-logo')),
        'logo_url' => wp_get_attachment_image_url($saved_attachment_id, 'full'),
        'background_type' => $background_type,
        'shortcode_mode' => $shortcode_mode
    ));
    exit;
}
add_action('wp_ajax_site_logo_update', 'site_logo_update_ajax_handler');


