        'currentUrl' => get_permalink(),
        'debug' => WCAG_DEBUG,
        'verbose' => WCAG_VERBOSE,
        'isAdmin' => $is_admin,
        'savedGrade' => $saved_grade,
        'savedScore' => $saved_score,
        'lastCheck' => $last_check,
        'logoUrls' => array(
            'A' => 'https://www.w3.org/WAI/wcag21/wcag2.1A-v.png',
            'AA' => 'https://www.w3.org/WAI/wcag21/wcag2.1AA-v.png',
