/**
 * AJAX handler for setting new password
 */
function dga_handle_set_new_password_vwx234() {
    // Verify nonce
    if (!wp_verify_nonce($_POST['nonce'], 'dga_password_reset_cf_nonce')) {
        wp_send_json_error(array(DGA_MESSAGE_KEY => __('‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÑ‡∏°‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á', DGA_TEXT_DOMAIN)));
    }
    
    $key = sanitize_text_field($_POST['key']);
    $login = sanitize_text_field($_POST['login']);
    $password = $_POST['password'];
    
    // Validate password strength
    if (strlen($password) < 8) {
        wp_send_json_error(array(DGA_MESSAGE_KEY => __('‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡∏ï‡πâ‡∏≠‡∏á‡∏°‡∏µ‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏ô‡πâ‡∏≠‡∏¢ 8 ‡∏ï‡∏±‡∏ß‡∏≠‡∏±‡∏Å‡∏©‡∏£', DGA_TEXT_DOMAIN)));
    }
    
    // Check password reset key
    $user = check_password_reset_key($key, $login);
    
    if (is_wp_error($user)) {
        wp_send_json_error(array(DGA_MESSAGE_KEY => __('‡∏•‡∏¥‡∏á‡∏Å‡πå‡∏£‡∏µ‡πÄ‡∏ã‡πá‡∏ó‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÑ‡∏°‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á‡∏´‡∏£‡∏∑‡∏≠‡∏´‡∏°‡∏î‡∏≠‡∏≤‡∏¢‡∏∏‡πÅ‡∏•‡πâ‡∏ß', DGA_TEXT_DOMAIN)));
    }
    
    // Reset password
    reset_password($user, $password);
    
    // Auto login
    wp_clear_auth_cookie();
    wp_set_current_user($user->ID);
    wp_set_auth_cookie($user->ID, true);
    
    // Get redirect URL
    $redirect_url = apply_filters('dga_password_reset_redirect', home_url(), $user);
    
    wp_send_json_success(array(
        DGA_MESSAGE_KEY => __('‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÉ‡∏´‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à ‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÄ‡∏Ç‡πâ‡∏≤‡∏™‡∏π‡πà‡∏£‡∏∞‡∏ö‡∏ö...', DGA_TEXT_DOMAIN),
        'redirect' => $redirect_url
    ));
}
add_action('wp_ajax_dga_set_new_password_cf', 'dga_handle_set_new_password_vwx234');
add_action('wp_ajax_nopriv_dga_set_new_password_cf', 'dga_handle_set_new_password_vwx234');





/***** MainMenu ********/

// Custom Walker ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÅ‡∏™‡∏î‡∏á‡πÄ‡∏°‡∏ô‡∏π‡πÅ‡∏ö‡∏ö dropdown
class Custom_Menu_Walker_mnu738 extends Walker_Nav_Menu {
    
    // ‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô sub-menu
    function start_lvl(&$output, $depth = 0, $args = null) {
        $indent = str_repeat("\t", $depth);
        $output .= "\n$indent<ul class=\"sub-menu-mnu738 depth-$depth\">\n";
    }
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á menu item
    function start_el(&$output, $item, $depth = 0, $args = null, $id = 0) {
        $indent = str_repeat("\t", $depth);
        
        // ‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£ CSS classes
        $classes = empty($item->classes) ? array() : (array) $item->classes;
        $classes[] = 'menu-item-' . $item->ID;
        
        // ‡πÄ‡∏û‡∏¥‡πà‡∏° class ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö item ‡∏ó‡∏µ‡πà‡∏°‡∏µ children
        if (in_array('menu-item-has-children', $classes)) {
            $classes[] = 'has-children-mnu738';
        }
        
        // Apply filters ‡πÅ‡∏•‡∏∞‡∏™‡∏£‡πâ‡∏≤‡∏á class string
        $class_names = join(' ', apply_filters('nav_menu_css_class', array_filter($classes), $item, $args, $depth));
        $class_names = $class_names ? ' class="' . esc_attr($class_names) . '"' : '';
        
        // ‡∏™‡∏£‡πâ‡∏≤‡∏á ID
        $id = apply_filters('nav_menu_item_id', 'menu-item-'. $item->ID, $item, $args, $depth);
        $id = $id ? ' id="' . esc_attr($id) . '"' : '';
        
        // ‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô list item
        $output .= $indent . '<li' . $id . $class_names .'>';
        
        // ‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£ attributes ‡∏Ç‡∏≠‡∏á link
        $atts = array();
        $atts[DGA_TITLE_FIELD]  = !empty($item->attr_title) ? $item->attr_title : '';
        $atts['target'] = !empty($item->target) ? $item->target : '';
        $atts['rel']    = !empty($item->xfn) ? $item->xfn : '';
        $atts['href']   = !empty($item->url) ? $item->url : '';
        
        // Apply filters ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö attributes
        $atts = apply_filters('nav_menu_link_attributes', $atts, $item, $args, $depth);
        
        // ‡∏™‡∏£‡πâ‡∏≤‡∏á attributes string
        $attributes = '';
        foreach ($atts as $attr => $value) {
            if (!empty($value)) {
                $value = ('href' === $attr) ? esc_url($value) : esc_attr($value);
                $attributes .= ' ' . $attr . '="' . $value . '"';
            }
        }
        
        // ‡∏™‡∏£‡πâ‡∏≤‡∏á link output
        $item_output = $args->before;
        $item_output .= '<a'. $attributes .'>';
        $item_output .= $args->link_before . apply_filters('the_title', $item->title, $item->ID) . $args->link_after;
        
        // ‡πÄ‡∏û‡∏¥‡πà‡∏° dropdown indicator ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö item ‡∏ó‡∏µ‡πà‡∏°‡∏µ children
        if (in_array('menu-item-has-children', $classes)) {
            $item_output .= ' <span class="dropdown-indicator-mnu738" aria-hidden="true"><i class="arrow-mnu738"></i></span>';
        }
        
        $item_output .= '</a>';
        $item_output .= $args->after;
        
        // Apply final filter
        $output .= apply_filters('walker_nav_menu_start_el', $item_output, $item, $depth, $args);
    }
}

// Shortcode function ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÅ‡∏™‡∏î‡∏á MainMenu
function custom_main_menu_shortcode_mnu738($atts) {
    // Extract attributes
    $atts = shortcode_atts(array(
        'menu' => 'MainMenu', // Default menu name
        'container_class' => 'custom-menu-container-mnu738'
    ), $atts, 'main_menu');
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏°‡∏µ‡πÄ‡∏°‡∏ô‡∏π‡∏≠‡∏¢‡∏π‡πà‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
    if (!wp_get_nav_menu_object($atts['menu'])) {
        return '<p>' . sprintf(
            __('Menu "%s" not found. Please create it in WordPress admin.', DGA_TEXT_DOMAIN),
            esc_html($atts['menu'])
        ) . '</p>';
    }
    
    // ‡πÄ‡∏£‡∏¥‡πà‡∏° output buffer
    ob_start();
    
    // ‡πÅ‡∏™‡∏î‡∏á‡πÄ‡∏°‡∏ô‡∏π
    wp_nav_menu(array(
        'menu'           => $atts['menu'],
        'menu_class'     => 'custom-main-menu-mnu738',
        'container'      => 'nav',
        'container_class'=> $atts['container_class'],
        'depth'          => 0, // ‡πÅ‡∏™‡∏î‡∏á‡∏ó‡∏∏‡∏Å‡∏£‡∏∞‡∏î‡∏±‡∏ö
        'walker'         => new Custom_Menu_Walker_mnu738(),
        'fallback_cb'    => false
    ));
    
    // Return output
    return ob_get_clean();
}
add_shortcode('main_menu', 'custom_main_menu_shortcode_mnu738');

