                    'complaint_closed' => 'secondary'
                ),
                'current_user' => array(
                    'id' => get_current_user_id(),
                    DGA_NAME_FIELD => wp_get_current_user()->display_name,
                    'can_edit' => current_user_can(DGA_EDIT_POSTS_CAP),
                    'can_delete' => current_user_can('delete_posts'),
