/**
 * 4. AJAX Handlers Enhanced
 */

/**
 * ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó‡πÉ‡∏´‡∏°‡πà‡∏û‡∏£‡πâ‡∏≠‡∏° preset
 */
function create_role_with_preset_ajax_xdk738() {
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö nonce
    if (!isset($_POST['nonce']) || !wp_verify_nonce($_POST['nonce'], 'department_role_enhanced_nonce')) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏£‡∏´‡∏±‡∏™‡∏Ñ‡∏ß‡∏≤‡∏°‡∏õ‡∏•‡∏≠‡∏î‡∏†‡∏±‡∏¢‡πÑ‡∏°‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á', DGA_TEXT_DOMAIN)]);
    }

    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå
    if (!current_user_can(DGA_MANAGE_OPTIONS_CAP)) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏Ñ‡∏∏‡∏ì‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏î‡∏≥‡πÄ‡∏ô‡∏¥‡∏ô‡∏Å‡∏≤‡∏£‡∏ô‡∏µ‡πâ', DGA_TEXT_DOMAIN)]);
    }

    $role_name = sanitize_text_field($_POST['role_name']);
    $display_name = sanitize_text_field($_POST['display_name']);
    $preset = sanitize_text_field($_POST['preset'] ?? '');
    
    if (empty($role_name) || empty($display_name)) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏´‡πâ‡∏Ñ‡∏£‡∏ö‡∏ñ‡πâ‡∏ß‡∏ô', DGA_TEXT_DOMAIN)]);
    }

    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó‡∏ã‡πâ‡∏≥
    if (get_role($role_name)) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏ä‡∏∑‡πà‡∏≠‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó‡∏ô‡∏µ‡πâ‡∏°‡∏µ‡∏≠‡∏¢‡∏π‡πà‡πÅ‡∏•‡πâ‡∏ß', DGA_TEXT_DOMAIN)]);
    }

    // ‡∏Å‡∏≥‡∏´‡∏ô‡∏î capabilities ‡∏ï‡∏≤‡∏° preset
    $capabilities = array('read' => true);
    if (!empty($preset)) {
        $presets = get_preset_templates_xdk738();
        if (isset($presets[$preset])) {
            foreach ($presets[$preset]['capabilities'] as $cap) {
                $capabilities[$cap] = true;
            }
        }
    }

    // ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó‡πÉ‡∏´‡∏°‡πà
    $result = add_role($role_name, $display_name, $capabilities);
    
    if ($result) {
        wp_send_json_success([DGA_MESSAGE_KEY => __('‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó‡πÉ‡∏´‡∏°‡πà‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢‡πÅ‡∏•‡πâ‡∏ß', DGA_TEXT_DOMAIN)]);
    } else {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó', DGA_TEXT_DOMAIN)]);
    }
}
add_action('wp_ajax_create_role_with_preset', 'create_role_with_preset_ajax_xdk738');

