    if (!current_user_can(DGA_MANAGE_OPTIONS_CAP)) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏Ñ‡∏∏‡∏ì‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏î‡∏≥‡πÄ‡∏ô‡∏¥‡∏ô‡∏Å‡∏≤‡∏£‡∏ô‡∏µ‡πâ', DGA_TEXT_DOMAIN)]);
    }

    $role_name = sanitize_text_field($_POST['role']);
    $new_name = sanitize_text_field($_POST['new_name'] ?? '');
    $capabilities = $_POST['capabilities'] ?? array();
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô
    $default_roles = [DGA_ADMIN_ROLE, 'editor', 'author', 'contributor', 'subscriber'];
    if (in_array($role_name, $default_roles)) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô‡πÑ‡∏î‡πâ', DGA_TEXT_DOMAIN)]);
    }

    $role = get_role($role_name);
    if (!$role) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó‡∏ó‡∏µ‡πà‡∏£‡∏∞‡∏ö‡∏∏', DGA_TEXT_DOMAIN)]);
    }

    // ‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï‡∏ä‡∏∑‡πà‡∏≠‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó‡∏ñ‡πâ‡∏≤‡∏°‡∏µ‡∏Å‡∏≤‡∏£‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô
    if (!empty($new_name) && $new_name !== $role_name) {
        // ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó‡πÉ‡∏´‡∏°‡πà‡∏î‡πâ‡∏ß‡∏¢‡∏ä‡∏∑‡πà‡∏≠‡πÉ‡∏´‡∏°‡πà
        $old_caps = $role->capabilities;
        remove_role($role_name);
        add_role($role_name, $new_name, $old_caps);
        $role = get_role($role_name);
    }

    // ‡∏•‡∏ö‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î‡∏Å‡πà‡∏≠‡∏ô (‡∏¢‡∏Å‡πÄ‡∏ß‡πâ‡∏ô read)
    foreach (array_keys($role->capabilities) as $cap) {
        if ($cap !== 'read') {
            $role->remove_cap($cap);
        }
    }

    // ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå‡πÉ‡∏´‡∏°‡πà
    foreach ($capabilities as $cap => $granted) {
        if ($granted === 'true' || $granted === true) {
            $role->add_cap($cap);
        }
    }

    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡πÉ‡∏´‡πâ‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏ß‡πà‡∏≤‡∏°‡∏µ read
    $role->add_cap('read');

    wp_send_json_success([DGA_MESSAGE_KEY => __('‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢‡πÅ‡∏•‡πâ‡∏ß', DGA_TEXT_DOMAIN)]);
}
