 */
function delete_role_with_reassign_ajax_xdk738() {
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö nonce
    if (!isset($_POST['nonce']) || !wp_verify_nonce($_POST['nonce'], 'department_role_enhanced_nonce')) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏£‡∏´‡∏±‡∏™‡∏Ñ‡∏ß‡∏≤‡∏°‡∏õ‡∏•‡∏≠‡∏î‡∏†‡∏±‡∏¢‡πÑ‡∏°‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á', DGA_TEXT_DOMAIN)]);
    }

    if (!current_user_can(DGA_MANAGE_OPTIONS_CAP)) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏Ñ‡∏∏‡∏ì‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏î‡∏≥‡πÄ‡∏ô‡∏¥‡∏ô‡∏Å‡∏≤‡∏£‡∏ô‡∏µ‡πâ', DGA_TEXT_DOMAIN)]);
    }

    $role = sanitize_text_field($_POST['role']);
    $reassign_role = sanitize_text_field($_POST['reassign_role'] ?? 'subscriber');
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô
    $default_roles = [DGA_ADMIN_ROLE, 'editor', 'author', 'contributor', 'subscriber'];
    if (in_array($role, $default_roles)) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏•‡∏ö‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô‡πÑ‡∏î‡πâ', DGA_TEXT_DOMAIN)]);
    }
    
    // ‡∏¢‡πâ‡∏≤‡∏¢‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ
    $users = get_users(['role' => $role]);
    foreach ($users as $user) {
        $user->remove_role($role);
        $user->add_role($reassign_role);
    }
    
    // ‡∏•‡∏ö‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó
    remove_role($role);
    
    wp_send_json_success([
        DGA_MESSAGE_KEY => sprintf(
            __('‡∏•‡∏ö‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢‡πÅ‡∏•‡πâ‡∏ß ‡∏¢‡πâ‡∏≤‡∏¢‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ %d ‡∏Ñ‡∏ô‡πÑ‡∏õ‡∏¢‡∏±‡∏á‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó %s', DGA_TEXT_DOMAIN),
            count($users),
            $reassign_role
        )
    ]);
}
add_action('wp_ajax_delete_role_with_reassign', 'delete_role_with_reassign_ajax_xdk738');

