 */
function get_users_by_role_ajax() {
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö nonce
    if (!isset($_POST['nonce']) || !wp_verify_nonce($_POST['nonce'], 'department_role_enhanced_nonce')) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏£‡∏´‡∏±‡∏™‡∏Ñ‡∏ß‡∏≤‡∏°‡∏õ‡∏•‡∏≠‡∏î‡∏†‡∏±‡∏¢‡πÑ‡∏°‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á', DGA_TEXT_DOMAIN)]);
    }
    
    if (!is_user_logged_in()) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡πÄ‡∏Ç‡πâ‡∏≤‡∏™‡∏π‡πà‡∏£‡∏∞‡∏ö‡∏ö', DGA_TEXT_DOMAIN)]);
    }
    
    if (!isset($_POST['role']) || empty($_POST['role'])) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏£‡∏∞‡∏ö‡∏∏‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó', DGA_TEXT_DOMAIN)]);
    }
    
    $role = sanitize_text_field($_POST['role']);
    $current_user = wp_get_current_user();
    $is_admin = current_user_can(DGA_MANAGE_OPTIONS_CAP);
    
    // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡∏ï‡∏≤‡∏°‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó
    $users = get_users(['role' => $role]);
    
    if (empty($users)) {
        wp_send_json_success([DGA_MESSAGE_KEY => __('‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡πÉ‡∏ô‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó‡∏ô‡∏µ‡πâ', DGA_TEXT_DOMAIN), 'html' => '<p>' . __('‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡πÉ‡∏ô‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó‡∏ô‡∏µ‡πâ', DGA_TEXT_DOMAIN) . '</p>']);
    }
    
    ob_start();
    ?>
    <table class="users-table">
        <thead>
            <tr>
                <th><?php _e('‡∏ä‡∏∑‡πà‡∏≠‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ', DGA_TEXT_DOMAIN); ?></th>
                <th><?php _e('‡∏ä‡∏∑‡πà‡∏≠-‡∏ô‡∏≤‡∏°‡∏™‡∏Å‡∏∏‡∏•', DGA_TEXT_DOMAIN); ?></th>
                <th><?php _e('‡∏≠‡∏µ‡πÄ‡∏°‡∏•', DGA_TEXT_DOMAIN); ?></th>
                <?php if ($is_admin): ?>
                <th><?php _e('‡∏Å‡∏≤‡∏£‡∏î‡∏≥‡πÄ‡∏ô‡∏¥‡∏ô‡∏Å‡∏≤‡∏£', DGA_TEXT_DOMAIN); ?></th>
                <?php endif; ?>
            </tr>
        </thead>
        <tbody>
            <?php foreach ($users as $user): ?>
            <tr>
                <td><?php echo esc_html($user->user_login); ?></td>
                <td><?php echo esc_html($user->display_name); ?></td>
                <td><?php echo esc_html($user->user_email); ?></td>
                <?php if ($is_admin): ?>
                <td>
                    <div class="action-buttons">
                        <a href="<?php echo esc_url(admin_url('user-edit.php?user_id=' . $user->ID)); ?>" class="edit-user-btn" target="_blank">
                            <?php _e('‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç', DGA_TEXT_DOMAIN); ?>
                        </a>
                    </div>
                </td>
                <?php endif; ?>
            </tr>
            <?php endforeach; ?>
        </tbody>
    </table>
    <?php
    $users_html = ob_get_clean();
    
    wp_send_json_success(['html' => $users_html]);
}





/****** ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô ‡πÅ‡∏•‡∏∞‡∏Å‡∏≥‡∏´‡∏ô‡∏î ROLE *****/

// ‡∏•‡∏á‡∏ó‡∏∞‡πÄ‡∏ö‡∏µ‡∏¢‡∏ô Shortcode ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÅ‡∏™‡∏î‡∏á‡∏ï‡∏≤‡∏£‡∏≤‡∏á‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ
function wp_user_manager_shortcode_hjk789() {
    // Enqueue CSS ‡πÅ‡∏•‡∏∞ JavaScript ‡∏à‡∏≤‡∏Å Child Theme
    wp_enqueue_style('wp-user-manager-style', get_stylesheet_directory_uri() . '/css/wp-user-manager.css');
    wp_enqueue_script('wp-user-manager-script', get_stylesheet_directory_uri() . '/js/wp-user-manager.js', array(DGA_JQUERY_HANDLE), null, true);
    
    // ‡∏™‡πà‡∏á‡∏Ñ‡πà‡∏≤‡∏ï‡πà‡∏≤‡∏á‡πÜ ‡πÑ‡∏õ‡∏¢‡∏±‡∏á JavaScript
    $roles = wp_roles()->get_names();
    
    // ‡πÄ‡∏£‡∏µ‡∏¢‡∏á‡∏•‡∏≥‡∏î‡∏±‡∏ö roles ‡∏ï‡∏≤‡∏° A-Z
    asort($roles);
    
