/**
 * ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏™‡∏£‡πâ‡∏≤‡∏á WordPress role ‡πÉ‡∏´‡∏°‡πà‡∏ó‡∏µ‡πà‡∏°‡∏µ‡∏Ñ‡∏ß‡∏≤‡∏°‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡πÄ‡∏ó‡πà‡∏≤‡∏Å‡∏±‡∏ö Administrator
 * 
 * @param string $role_name ‡∏ä‡∏∑‡πà‡∏≠‡∏Ç‡∏≠‡∏á role ‡πÉ‡∏´‡∏°‡πà (slug)
 * @param string $display_name ‡∏ä‡∏∑‡πà‡∏≠‡∏ó‡∏µ‡πà‡πÅ‡∏™‡∏î‡∏á‡∏Ç‡∏≠‡∏á role ‡πÉ‡∏´‡∏°‡πà
 * @return WP_Role|WP_Error ‡∏≠‡∏≠‡∏ö‡πÄ‡∏à‡∏Å‡∏ï‡πå role ‡πÉ‡∏´‡∏°‡πà‡∏´‡∏£‡∏∑‡∏≠ WP_Error ‡πÉ‡∏ô‡∏Å‡∏£‡∏ì‡∏µ‡∏ó‡∏µ‡πà‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î
 */
function create_role_with_admin_capabilities_hjk789($role_name, $display_name) {
    // ‡∏î‡∏∂‡∏á role administrator
    $admin_role = get_role(DGA_ADMIN_ROLE);
    
    // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏°‡∏µ role administrator ‡πÉ‡∏´‡πâ return error
    if (!$admin_role) {
        return new WP_Error('no_admin_role', __('‡πÑ‡∏°‡πà‡∏û‡∏ö role Administrator', DGA_TEXT_DOMAIN));
    }
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤ role ‡∏ó‡∏µ‡πà‡∏à‡∏∞‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏°‡∏µ‡∏≠‡∏¢‡∏π‡πà‡πÅ‡∏•‡πâ‡∏ß‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
    $existing_role = get_role($role_name);
    
    // ‡∏ñ‡πâ‡∏≤ role ‡∏°‡∏µ‡∏≠‡∏¢‡∏π‡πà‡πÅ‡∏•‡πâ‡∏ß ‡πÉ‡∏´‡πâ‡∏•‡∏ö‡∏≠‡∏≠‡∏Å‡∏Å‡πà‡∏≠‡∏ô‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏™‡∏£‡πâ‡∏≤‡∏á‡πÉ‡∏´‡∏°‡πà
    if ($existing_role) {
        remove_role($role_name);
    }
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á role ‡πÉ‡∏´‡∏°‡πà‡πÇ‡∏î‡∏¢‡πÉ‡∏ä‡πâ‡∏Ñ‡∏ß‡∏≤‡∏°‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡πÄ‡∏î‡∏µ‡∏¢‡∏ß‡∏Å‡∏±‡∏ö administrator
    $new_role = add_role($role_name, $display_name, $admin_role->capabilities);
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
    if (!$new_role) {
        return new WP_Error('role_creation_failed', __('‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏™‡∏£‡πâ‡∏≤‡∏á role ‡πÉ‡∏´‡∏°‡πà‡πÑ‡∏î‡πâ', DGA_TEXT_DOMAIN));
    }
    
    return $new_role;
}

/**
 * ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡∏Ñ‡∏ß‡∏≤‡∏°‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏Ç‡∏≠‡∏á Administrator ‡πÉ‡∏´‡πâ‡∏Å‡∏±‡∏ö role ‡∏ó‡∏µ‡πà‡∏°‡∏µ‡∏≠‡∏¢‡∏π‡πà‡πÅ‡∏•‡πâ‡∏ß
 * 
 * @param string $role_name ‡∏ä‡∏∑‡πà‡∏≠‡∏Ç‡∏≠‡∏á role ‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡∏Ñ‡∏ß‡∏≤‡∏°‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ
 * @return WP_Role|WP_Error ‡∏≠‡∏≠‡∏ö‡πÄ‡∏à‡∏Å‡∏ï‡πå role ‡∏ó‡∏µ‡πà‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï‡πÅ‡∏•‡πâ‡∏ß‡∏´‡∏£‡∏∑‡∏≠ WP_Error ‡πÉ‡∏ô‡∏Å‡∏£‡∏ì‡∏µ‡∏ó‡∏µ‡πà‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î
 */
function assign_admin_capabilities_to_role_hjk789($role_name) {
    // ‡∏î‡∏∂‡∏á role ‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£
    $role = get_role($role_name);
    
    // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏û‡∏ö role ‡πÉ‡∏´‡πâ return error
    if (!$role) {
        return new WP_Error('role_not_found', sprintf(__('‡πÑ‡∏°‡πà‡∏û‡∏ö role "%s"', DGA_TEXT_DOMAIN), $role_name));
    }
    
    // ‡∏î‡∏∂‡∏á role administrator
    $admin_role = get_role(DGA_ADMIN_ROLE);
    
    // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏°‡∏µ role administrator ‡πÉ‡∏´‡πâ return error
    if (!$admin_role) {
        return new WP_Error('no_admin_role', __('‡πÑ‡∏°‡πà‡∏û‡∏ö role Administrator', DGA_TEXT_DOMAIN));
    }
    
    // ‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡∏Ñ‡∏ß‡∏≤‡∏°‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏Ç‡∏≠‡∏á administrator ‡πÉ‡∏´‡πâ‡∏Å‡∏±‡∏ö role
    foreach ($admin_role->capabilities as $cap => $grant) {
        $role->add_cap($cap, $grant);
    }
    
    return $role;
}

/**
