    // Enqueue CSS
    wp_enqueue_style(
        'pplist-ppl738',
        $theme_uri . '/css/pplist-ppl738.css',
        array(),
        $version
    );
    
    // Enqueue JS
    wp_enqueue_script(
        'pplist-ppl738',
