function tdep_is_new_post($post_time) {
    $current_time = current_time('timestamp');
    $hours_diff = ($current_time - $post_time) / 3600; // ‡πÅ‡∏õ‡∏•‡∏á‡∏ß‡∏¥‡∏ô‡∏≤‡∏ó‡∏µ‡πÄ‡∏õ‡πá‡∏ô‡∏ä‡∏±‡πà‡∏ß‡πÇ‡∏°‡∏á
    return $hours_diff <= 12;
}

