// Callback function for all MPeople posts
function get_mpeople_posts() {
    $args = array(
        DGA_POST_TYPE_FIELD => 'mpeople',
        DGA_POSTS_PER_PAGE => -1,
        DGA_POST_STATUS_FIELD => DGA_PUBLISH_STATUS
    );

    $posts = get_posts($args);
    $data = array();

