        echo '<div class="dynamic-table-container view-only">';
        echo '<h2 class="dynamic-table-title">' . esc_html($atts[DGA_TITLE_FIELD]) . '</h2>';
        echo dynamic_table_display($atts['id']);
        echo '</div>';
    }
    
    // Return output buffer
    return ob_get_clean();
}
