// AJAX ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÇ‡∏´‡∏•‡∏î‡πÇ‡∏û‡∏™
function user_posts_load_ajax() {
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö Security nonce
    if (!isset($_POST['nonce']) || !wp_verify_nonce($_POST['nonce'], 'user_posts_nonce')) {
        wp_send_json_error(array(DGA_MESSAGE_KEY => '‡∏Å‡∏≤‡∏£‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ñ‡∏ß‡∏≤‡∏°‡∏õ‡∏•‡∏≠‡∏î‡∏†‡∏±‡∏¢‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß'));
    }
    
    // ‡∏î‡∏∂‡∏á ID ‡∏Ç‡∏≠‡∏á‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡∏ó‡∏µ‡πà‡∏•‡πá‡∏≠‡∏Å‡∏≠‡∏¥‡∏ô
    $user_id = get_current_user_id();
    if (!$user_id) {
        wp_send_json_error(array(DGA_MESSAGE_KEY => '‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ'));
    }
    
    // ‡∏î‡∏∂‡∏á‡∏û‡∏≤‡∏£‡∏≤‡∏°‡∏¥‡πÄ‡∏ï‡∏≠‡∏£‡πå
    $page = isset($_POST['page']) ? intval($_POST['page']) : 1;
    $per_page = isset($_POST['per_page']) ? intval($_POST['per_page']) : 10;
    $search = isset($_POST['search']) ? sanitize_text_field($_POST['search']) : '';
    $post_type = isset($_POST[DGA_POST_TYPE_FIELD]) ? sanitize_text_field($_POST[DGA_POST_TYPE_FIELD]) : '';
    $post_status = isset($_POST[DGA_POST_STATUS_FIELD]) ? sanitize_text_field($_POST[DGA_POST_STATUS_FIELD]) : '';
    
    // ‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡∏û‡∏≤‡∏£‡∏≤‡∏°‡∏¥‡πÄ‡∏ï‡∏≠‡∏£‡πå‡∏Å‡∏≤‡∏£‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤
    $args = array(
        'author' => $user_id,
        DGA_POSTS_PER_PAGE => $per_page,
        DGA_PAGED_PARAMETER => $page,
        DGA_POST_STATUS_FIELD => $post_status ? $post_status : array(DGA_PUBLISH_STATUS, 'pending', 'draft'),
        DGA_ORDERBY_FIELD_VALUE => 'date',
        DGA_ORDER_FIELD => 'DESC',
    );
    
    // ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡πÇ‡∏û‡∏™‡∏ñ‡πâ‡∏≤‡∏°‡∏µ‡∏Å‡∏≤‡∏£‡∏£‡∏∞‡∏ö‡∏∏
    if (!empty($post_type)) {
        $args[DGA_POST_TYPE_FIELD] = $post_type;
    } else {
        $args[DGA_POST_TYPE_FIELD] = 'any';
    }
    
    // ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏Ñ‡∏≥‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡∏ñ‡πâ‡∏≤‡∏°‡∏µ‡∏Å‡∏≤‡∏£‡∏£‡∏∞‡∏ö‡∏∏
    if (!empty($search)) {
        $args['s'] = $search;
    }
    
    // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÇ‡∏û‡∏™
    $query = new WP_Query($args);
    
    // ‡πÄ‡∏ï‡∏£‡∏µ‡∏¢‡∏°‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÇ‡∏û‡∏™
    $posts_data = array();
    
    if ($query->have_posts()) {
        while ($query->have_posts()) {
            $query->the_post();
            $post_id = get_the_ID();
            $post_type_obj = get_post_type_object(get_post_type());
            
            $posts_data[] = array(
                'id' => $post_id,
                DGA_TYPE_FIELD => $post_type_obj ? $post_type_obj->labels->singular_name : get_post_type(),
                DGA_TITLE_FIELD => get_the_title(),
                'date' => get_the_date(),
                DGA_STATUS_FIELD => get_post_status(),
                'edit_link' => get_edit_post_link($post_id),
                'view_link' => get_permalink($post_id),
            );
        }
    }
    
    // ‡∏Ñ‡∏∑‡∏ô‡∏Ñ‡πà‡∏≤ Post Data
    wp_reset_postdata();
    
    // ‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö Pagination
    $total_posts = $query->found_posts;
    $total_pages = ceil($total_posts / $per_page);
    
    // ‡∏™‡πà‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏Å‡∏•‡∏±‡∏ö - ‡πÑ‡∏°‡πà‡∏™‡∏£‡πâ‡∏≤‡∏á HTML ‡πÉ‡∏ô PHP ‡πÅ‡∏•‡πâ‡∏ß ‡πÅ‡∏ï‡πà‡πÉ‡∏´‡πâ JS ‡∏™‡∏£‡πâ‡∏≤‡∏á‡πÅ‡∏ó‡∏ô
    wp_send_json_success(array(
        'posts' => $posts_data,
        'total_posts' => $total_posts,
        'total_pages' => $total_pages,
        'current_page' => $page,
        'per_page' => $per_page
    ));
}
add_action('wp_ajax_user_posts_load', 'user_posts_load_ajax');

// AJAX ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏≠‡∏±‡∏û‡πÄ‡∏î‡∏ó‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞‡πÇ‡∏û‡∏™
function user_posts_update_status_ajax() {
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö Security nonce
    if (!isset($_POST['nonce']) || !wp_verify_nonce($_POST['nonce'], 'user_posts_nonce')) {
        wp_send_json_error(array(DGA_MESSAGE_KEY => '‡∏Å‡∏≤‡∏£‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ñ‡∏ß‡∏≤‡∏°‡∏õ‡∏•‡∏≠‡∏î‡∏†‡∏±‡∏¢‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß'));
    }
    
    // ‡∏î‡∏∂‡∏á‡∏û‡∏≤‡∏£‡∏≤‡∏°‡∏¥‡πÄ‡∏ï‡∏≠‡∏£‡πå
    $post_id = isset($_POST[DGA_POST_ID_FIELD]) ? intval($_POST[DGA_POST_ID_FIELD]) : 0;
    $new_status = isset($_POST[DGA_STATUS_FIELD]) ? sanitize_text_field($_POST[DGA_STATUS_FIELD]) : '';
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ñ‡∏ß‡∏≤‡∏°‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á‡∏Ç‡∏≠‡∏á‡∏û‡∏≤‡∏£‡∏≤‡∏°‡∏¥‡πÄ‡∏ï‡∏≠‡∏£‡πå
    if (!$post_id || !in_array($new_status, array(DGA_PUBLISH_STATUS, 'pending'))) {
        wp_send_json_error(array(DGA_MESSAGE_KEY => '‡∏û‡∏≤‡∏£‡∏≤‡∏°‡∏¥‡πÄ‡∏ï‡∏≠‡∏£‡πå‡πÑ‡∏°‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á'));
    }
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡πÄ‡∏õ‡πá‡∏ô‡πÄ‡∏à‡πâ‡∏≤‡∏Ç‡∏≠‡∏á‡πÇ‡∏û‡∏™‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
    $post = get_post($post_id);
    if (!$post || $post->post_author != get_current_user_id()) {
        wp_send_json_error(array(DGA_MESSAGE_KEY => '‡∏Ñ‡∏∏‡∏ì‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‡πÇ‡∏û‡∏™‡∏ô‡∏µ‡πâ'));
    }
    
    // ‡∏≠‡∏±‡∏û‡πÄ‡∏î‡∏ó‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞‡πÇ‡∏û‡∏™
    $updated = wp_update_post(array(
        'ID' => $post_id,
        DGA_POST_STATUS_FIELD => $new_status
    ));
    
    if ($updated) {
        wp_send_json_success(array(
            DGA_MESSAGE_KEY => '‡∏≠‡∏±‡∏û‡πÄ‡∏î‡∏ó‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞‡πÇ‡∏û‡∏™‡πÄ‡∏õ‡πá‡∏ô ' . ($new_status == DGA_PUBLISH_STATUS ? '‡πÄ‡∏ú‡∏¢‡πÅ‡∏û‡∏£‡πà‡πÅ‡∏•‡πâ‡∏ß' : '‡∏£‡∏≠‡∏≠‡∏ô‡∏∏‡∏°‡∏±‡∏ï‡∏¥'),
            'new_status' => $new_status
        ));
    } else {
        wp_send_json_error(array(DGA_MESSAGE_KEY => '‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏≠‡∏±‡∏û‡πÄ‡∏î‡∏ó‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞‡πÇ‡∏û‡∏™‡πÑ‡∏î‡πâ'));
    }
}
add_action('wp_ajax_user_posts_update_status', 'user_posts_update_status_ajax');

// AJAX ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏•‡∏ö‡πÇ‡∏û‡∏™
function user_posts_delete_ajax() {
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö Security nonce
    if (!isset($_POST['nonce']) || !wp_verify_nonce($_POST['nonce'], 'user_posts_nonce')) {
        wp_send_json_error(array(DGA_MESSAGE_KEY => '‡∏Å‡∏≤‡∏£‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ñ‡∏ß‡∏≤‡∏°‡∏õ‡∏•‡∏≠‡∏î‡∏†‡∏±‡∏¢‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß'));
    }
    
    // ‡∏î‡∏∂‡∏á‡∏û‡∏≤‡∏£‡∏≤‡∏°‡∏¥‡πÄ‡∏ï‡∏≠‡∏£‡πå
    $post_id = isset($_POST[DGA_POST_ID_FIELD]) ? intval($_POST[DGA_POST_ID_FIELD]) : 0;
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡πÇ‡∏û‡∏™‡∏°‡∏µ‡∏≠‡∏¢‡∏π‡πà‡∏à‡∏£‡∏¥‡∏á‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
    $post = get_post($post_id);
    if (!$post) {
        wp_send_json_error(array(DGA_MESSAGE_KEY => '‡πÑ‡∏°‡πà‡∏û‡∏ö‡πÇ‡∏û‡∏™‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö'));
    }
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡πÄ‡∏õ‡πá‡∏ô‡πÄ‡∏à‡πâ‡∏≤‡∏Ç‡∏≠‡∏á‡πÇ‡∏û‡∏™‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
    if ($post->post_author != get_current_user_id()) {
        wp_send_json_error(array(DGA_MESSAGE_KEY => '‡∏Ñ‡∏∏‡∏ì‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå‡∏•‡∏ö‡πÇ‡∏û‡∏™‡∏ô‡∏µ‡πâ'));
    }
    
    // ‡πÄ‡∏Å‡πá‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÇ‡∏û‡∏™‡∏Å‡πà‡∏≠‡∏ô‡∏•‡∏ö‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÉ‡∏ä‡πâ‡πÉ‡∏ô‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏°‡∏ï‡∏≠‡∏ö‡∏Å‡∏•‡∏±‡∏ö
    $post_title = $post->post_title;
    
    // ‡∏•‡∏ö‡πÇ‡∏û‡∏™
    $deleted = wp_delete_post($post_id, true); // true = ‡∏•‡∏ö‡∏ñ‡∏≤‡∏ß‡∏£, ‡πÑ‡∏°‡πà‡∏¢‡πâ‡∏≤‡∏¢‡πÑ‡∏õ‡∏ñ‡∏±‡∏á‡∏Ç‡∏¢‡∏∞
    
    if ($deleted) {
        wp_send_json_success(array(
            DGA_MESSAGE_KEY => '‡∏•‡∏ö‡πÇ‡∏û‡∏™ "' . esc_html($post_title) . '" ‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢‡πÅ‡∏•‡πâ‡∏ß',
        ));
    } else {
        wp_send_json_error(array(DGA_MESSAGE_KEY => '‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏•‡∏ö‡πÇ‡∏û‡∏™‡πÑ‡∏î‡πâ ‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏•‡∏≠‡∏á‡πÉ‡∏´‡∏°‡πà‡∏≠‡∏µ‡∏Å‡∏Ñ‡∏£‡∏±‡πâ‡∏á'));
    }
}
add_action('wp_ajax_user_posts_delete', 'user_posts_delete_ajax');



