                $output .= '<option value="title-asc">' . __('‡∏ä‡∏∑‡πà‡∏≠‡πÄ‡∏£‡∏∑‡πà‡∏≠‡∏á (‡∏Å-‡∏Æ)', DGA_TEXT_DOMAIN) . '</option>';
                $output .= '<option value="title-desc">' . __('‡∏ä‡∏∑‡πà‡∏≠‡πÄ‡∏£‡∏∑‡πà‡∏≠‡∏á (‡∏Æ-‡∏Å)', DGA_TEXT_DOMAIN) . '</option>';
