/**
 * ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡πà‡∏ô‡∏î‡∏∂‡∏á Taxonomies ‡∏ó‡∏µ‡πà‡πÄ‡∏Å‡∏µ‡πà‡∏¢‡∏ß‡∏Ç‡πâ‡∏≠‡∏á‡∏Å‡∏±‡∏ö post type
 */
function wptax_get_posttype_taxonomies($post_type) {
    $taxonomies = get_object_taxonomies($post_type, 'objects');
    
    // ‡∏Å‡∏£‡∏≠‡∏á‡πÄ‡∏≠‡∏≤‡πÄ‡∏â‡∏û‡∏≤‡∏∞ taxonomy ‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡πÅ‡∏™‡∏î‡∏á (‡πÑ‡∏°‡πà‡∏£‡∏ß‡∏° taxonomy ‡∏Ç‡∏≠‡∏á‡∏£‡∏∞‡∏ö‡∏ö)
    $exclude_taxonomies = array('post_format', 'wp_theme', 'wp_template_part_area');
    foreach ($exclude_taxonomies as $exclude) {
        if (isset($taxonomies[$exclude])) {
            unset($taxonomies[$exclude]);
        }
    }
    
    return $taxonomies;
}

/**
 * ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡πà‡∏ô‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤ post ‡∏°‡∏µ‡∏Å‡∏≤‡∏£‡∏Å‡∏≥‡∏´‡∏ô‡∏î term ‡πÉ‡∏ô taxonomy ‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
 */
function wptax_post_has_terms($post_id, $taxonomy) {
    $terms = wp_get_post_terms($post_id, $taxonomy);
    return !empty($terms) && !is_wp_error($terms);
}

/**
 * Shortcode function
 */
function wptax_category_modal_shortcode($atts) {
    // ‡πÑ‡∏°‡πà‡∏ó‡∏≥‡∏á‡∏≤‡∏ô‡∏Å‡∏±‡∏ö post type "ckan"
    if (get_post_type() === 'ckan') {
        return '';
    }
    
    // Parse attributes
    $atts = shortcode_atts(array(
        DGA_TAXONOMY_FIELD => '', // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏£‡∏∞‡∏ö‡∏∏‡∏à‡∏∞‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ó‡∏∏‡∏Å taxonomy
    ), $atts);
    
    // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ login ‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç ‡πÑ‡∏°‡πà‡∏ï‡πâ‡∏≠‡∏á‡πÅ‡∏™‡∏î‡∏á‡∏≠‡∏∞‡πÑ‡∏£
    if (!is_user_logged_in() || !current_user_can(DGA_EDIT_POSTS_CAP)) {
        return '';
    }
    
    $post_id = get_the_ID();
    $post_type = get_post_type($post_id);
    
    // ‡∏î‡∏∂‡∏á taxonomies ‡∏ó‡∏µ‡πà‡πÄ‡∏Å‡∏µ‡πà‡∏¢‡∏ß‡∏Ç‡πâ‡∏≠‡∏á‡∏Å‡∏±‡∏ö post type ‡∏ô‡∏µ‡πâ
    if (!empty($atts[DGA_TAXONOMY_FIELD])) {
        // ‡∏ñ‡πâ‡∏≤‡∏°‡∏µ‡∏Å‡∏≤‡∏£‡∏£‡∏∞‡∏ö‡∏∏ taxonomy ‡πÉ‡∏´‡πâ‡πÉ‡∏ä‡πâ‡πÄ‡∏â‡∏û‡∏≤‡∏∞ taxonomy ‡∏ó‡∏µ‡πà‡∏£‡∏∞‡∏ö‡∏∏
        $taxonomy_obj = get_taxonomy($atts[DGA_TAXONOMY_FIELD]);
        if ($taxonomy_obj) {
            $taxonomies = array($atts[DGA_TAXONOMY_FIELD] => $taxonomy_obj);
        } else {
            return '<div class="wptax-error">‡πÑ‡∏°‡πà‡∏û‡∏ö Taxonomy ‡∏ó‡∏µ‡πà‡∏£‡∏∞‡∏ö‡∏∏</div>';
        }
    } else {
        // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏£‡∏∞‡∏ö‡∏∏ ‡πÉ‡∏´‡πâ‡∏î‡∏∂‡∏á‡∏ó‡∏∏‡∏Å taxonomy ‡∏ó‡∏µ‡πà‡πÄ‡∏Å‡∏µ‡πà‡∏¢‡∏ß‡∏Ç‡πâ‡∏≠‡∏á
        $taxonomies = wptax_get_posttype_taxonomies($post_type);
    }
    
    if (empty($taxonomies)) {
        return '<div class="wptax-error">‡πÑ‡∏°‡πà‡∏û‡∏ö Taxonomy ‡∏ó‡∏µ‡πà‡πÄ‡∏Å‡∏µ‡πà‡∏¢‡∏ß‡∏Ç‡πâ‡∏≠‡∏á</div>';
    }
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏°‡∏µ term ‡πÉ‡∏ô taxonomy ‡∏´‡∏£‡∏∑‡∏≠‡∏¢‡∏±‡∏á
    $missing_taxonomies = array();
    foreach ($taxonomies as $tax_name => $tax_obj) {
        if (!wptax_post_has_terms($post_id, $tax_name)) {
            $missing_taxonomies[$tax_name] = $tax_obj;
        }
    }
    
    // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏°‡∏µ taxonomy ‡∏ó‡∏µ‡πà‡∏Ç‡∏≤‡∏î‡∏´‡∏°‡∏ß‡∏î‡∏´‡∏°‡∏π‡πà ‡πÑ‡∏°‡πà‡∏ï‡πâ‡∏≠‡∏á‡πÅ‡∏™‡∏î‡∏á modal
    if (empty($missing_taxonomies)) {
        return '';
    }
    
    // ‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏™‡∏£‡πâ‡∏≤‡∏á output
    ob_start();
    ?>
    <div id="wptax-category-modal" class="wptax-modal" data-post-id="<?php echo esc_attr($post_id); ?>">
        <div class="wptax-modal-overlay"></div>
        <div class="wptax-modal-container">
            <div class="wptax-modal-header">
                <h3>‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡∏´‡∏°‡∏ß‡∏î‡∏´‡∏°‡∏π‡πà‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÄ‡∏ô‡∏∑‡πâ‡∏≠‡∏´‡∏≤‡∏ô‡∏µ‡πâ</h3>
                <button class="wptax-modal-close">&times;</button>
