    // ‡∏™‡πà‡∏á‡∏ú‡∏•‡∏•‡∏±‡∏û‡∏ò‡πå‡∏Å‡∏•‡∏±‡∏ö
    $content_index = count($indicators[$indicator_index]['content_items']) - 1;
    wp_send_json_success(array(
        'content_index' => $content_index,
        'content' => $indicators[$indicator_index]['content_items'][$content_index]
    ));
}
