// Update item order via AJAX
function org_links_update_order() {
    check_ajax_referer('org_links_admin_nonce', 'nonce');
    
    if (!current_user_can(DGA_MANAGE_OPTIONS_CAP)) {
        wp_send_json_error(array(DGA_MESSAGE_KEY => '‡∏Ñ‡∏∏‡∏ì‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå‡πÄ‡∏û‡∏µ‡∏¢‡∏á‡∏û‡∏≠'));
        return;
    }
    
    $items = isset($_POST['items']) ? $_POST['items'] : array();
    
    if (empty($items)) {
        wp_send_json_error(array(DGA_MESSAGE_KEY => '‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏à‡∏∞‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å'));
        return;
    }
    
    global $wpdb;
    $table_name = $wpdb->prefix . 'organization_links';
    
    $success = true;
    
    foreach ($items as $index => $id) {
        $id = intval($id);
        $order = intval($index);
        
        $result = $wpdb->update(
            $table_name,
            array('order_num' => $order),
            array('id' => $id),
            array('%d'),
            array('%d')
        );
        
        if ($result === false) {
            $success = false;
        }
    }
    
    if ($success) {
        wp_send_json_success(array(DGA_MESSAGE_KEY => '‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Å‡∏≤‡∏£‡πÄ‡∏£‡∏µ‡∏¢‡∏á‡∏•‡∏≥‡∏î‡∏±‡∏ö‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢‡πÅ‡∏•‡πâ‡∏ß'));
    } else {
        wp_send_json_error(array(DGA_MESSAGE_KEY => '‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏•‡∏≥‡∏î‡∏±‡∏ö'));
    }
}
add_action('wp_ajax_org_links_update_order', 'org_links_update_order');

