// Add AJAX handler for form submission
function org_links_add_item() {
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö nonce
    if (!isset($_POST['nonce']) || !wp_verify_nonce($_POST['nonce'], 'org_links_admin')) {
        wp_send_json_error(array(DGA_MESSAGE_KEY => '‡∏Å‡∏≤‡∏£‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ñ‡∏ß‡∏≤‡∏°‡∏õ‡∏•‡∏≠‡∏î‡∏†‡∏±‡∏¢‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß'));
        return;
    }
    
    if (!current_user_can(DGA_MANAGE_OPTIONS_CAP)) {
        wp_send_json_error(array(DGA_MESSAGE_KEY => '‡∏Ñ‡∏∏‡∏ì‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå‡πÄ‡∏û‡∏µ‡∏¢‡∏á‡∏û‡∏≠'));
        return;
    }
    
    // ‡∏î‡∏∂‡∏á‡πÅ‡∏•‡∏∞‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•
    $title = isset($_POST[DGA_TITLE_FIELD]) ? sanitize_text_field($_POST[DGA_TITLE_FIELD]) : '';
    $url = isset($_POST['url']) ? esc_url_raw($_POST['url']) : '';
    $logo_url = isset($_POST['logo_url']) ? esc_url_raw($_POST['logo_url']) : '';
    $type = isset($_POST['type']) ? sanitize_text_field($_POST['type']) : 'internal';
    
    if (empty($title) || empty($url) || empty($logo_url)) {
        wp_send_json_error(array(DGA_MESSAGE_KEY => '‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏´‡πâ‡∏Ñ‡∏£‡∏ö‡∏ñ‡πâ‡∏ß‡∏ô'));
        return;
    }
    
    try {
        global $wpdb;
        $table_name = $wpdb->prefix . 'organization_links';
        
        // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏ï‡∏≤‡∏£‡∏≤‡∏á‡∏°‡∏µ‡∏≠‡∏¢‡∏π‡πà‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
        if ($wpdb->get_var("SHOW TABLES LIKE '$table_name'") != $table_name) {
            org_links_create_table();
        }
        
        // ‡∏î‡∏∂‡∏á‡∏•‡∏≥‡∏î‡∏±‡∏ö‡∏™‡∏π‡∏á‡∏™‡∏∏‡∏î
        $max_order = $wpdb->get_var("SELECT MAX(order_num) FROM $table_name");
        $order_num = $max_order ? $max_order + 1 : 1;
        
        // ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏´‡∏°‡πà
        $result = $wpdb->insert(
            $table_name,
            array(
                DGA_TITLE_FIELD => $title,
                'logo' => $logo_url,
                'url' => $url,
                DGA_TYPE_FIELD => $type,
                'order_num' => $order_num
            ),
            array('%s', '%s', '%s', '%s', '%d')
        );
        
        if ($result) {
            // ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à
            wp_send_json_success(array(
                DGA_MESSAGE_KEY => '‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏´‡∏ô‡πà‡∏ß‡∏¢‡∏á‡∏≤‡∏ô‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢‡πÅ‡∏•‡πâ‡∏ß',
                'item' => array(
                    'id' => $wpdb->insert_id,
                    DGA_TITLE_FIELD => $title,
                    'logo' => $logo_url,
                    'url' => $url,
                    DGA_TYPE_FIELD => $type,
                    'order_num' => $order_num
                )
            ));
        } else {
            // ‡∏°‡∏µ‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î
            wp_send_json_error(array(
                DGA_MESSAGE_KEY => '‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•: ' . $wpdb->last_error
            ));
        }
        
    } catch (Exception $e) {
        // ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÅ‡∏•‡∏∞‡∏™‡πà‡∏á‡∏Ñ‡∏∑‡∏ô‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏°‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î
        error_log('Organization Links Error: ' . $e->getMessage());
        wp_send_json_error(array(
            DGA_MESSAGE_KEY => '‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏£‡∏∞‡∏ö‡∏ö: ' . $e->getMessage()
        ));
    }
}
add_action('wp_ajax_org_links_add_item', 'org_links_add_item');


