    // Display assets if they exist
    if ($ckan_assets && is_array($ckan_assets) && count($ckan_assets) > 0) {
        foreach ($ckan_assets as $index => $asset) {
            $name = isset($asset['ckan_asset_name']) ? $asset['ckan_asset_name'] : '';
