/**** CKAN API ******/

function ckan_register_api_routes() {
    // ‡πÄ‡∏£‡∏µ‡∏¢‡∏Å‡∏î‡∏π‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏Ç‡∏≠‡∏á Post ‡∏´‡∏ô‡∏∂‡πà‡∏á‡πÜ
    register_rest_route('ckan/v1', '/data/(?P<id>\d+)', array(
        'methods' => 'GET',
        'callback' => 'ckan_get_post_data',
        'permission_callback' => '__return_true',
    ));
    
    // ‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•
    register_rest_route('ckan/v1', '/search', array(
        'methods' => 'GET',
        'callback' => 'ckan_search_data',
        'permission_callback' => '__return_true',
    ));
    
    // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏≤‡∏Å‡πÑ‡∏ü‡∏•‡πå‡πÇ‡∏î‡∏¢‡∏ï‡∏£‡∏á
    register_rest_route('ckan/v1', '/file/(?P<post_id>\d+)/(?P<file_index>\d+)', array(
        'methods' => 'GET',
        'callback' => 'ckan_get_file_data',
        'permission_callback' => '__return_true',
    ));
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏´‡∏°‡πà
    register_rest_route('ckan/v1', '/create', array(
        'methods' => 'POST',
        'callback' => 'ckan_create_data',
        'permission_callback' => function() {
            return current_user_can(DGA_EDIT_POSTS_CAP);
        },
    ));
    
    // ‡∏≠‡∏±‡∏û‡πÄ‡∏î‡∏ó‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•
    register_rest_route('ckan/v1', '/upsert', array(
        'methods' => 'POST',
        'callback' => 'ckan_upsert_data',
        'permission_callback' => function() {
            return current_user_can(DGA_EDIT_POSTS_CAP);
        },
    ));
}
add_action('rest_api_init', 'ckan_register_api_routes');

// ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏Ç‡∏≠‡∏á Post
function ckan_get_post_data($request) {
    // ‡∏£‡∏±‡∏ö‡∏Ñ‡πà‡∏≤ post ID
    $post_id = $request['id'];
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡πÇ‡∏û‡∏™‡∏ï‡πå‡∏°‡∏µ‡∏≠‡∏¢‡∏π‡πà‡∏à‡∏£‡∏¥‡∏á
    $post = get_post($post_id);
    if (!$post) {
        return new WP_Error('post_not_found', '‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö ID ‡∏ô‡∏µ‡πâ', array(DGA_STATUS_FIELD => 404));
    }
    
    // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏• metadata ‡∏à‡∏≤‡∏Å ACF fields
    $metadata_fields = ckan_get_metadata_fields();
    $metadata = array();
    
    foreach ($metadata_fields as $field) {
        $field_value = get_field($field[DGA_FIELD_KEY], $post_id);
        $metadata[$field[DGA_FIELD_KEY]] = $field_value;
    }
    
    // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÑ‡∏ü‡∏•‡πå‡πÅ‡∏ô‡∏ö‡∏à‡∏≤‡∏Å Repeater field
    $ckan_assets = get_field('ckan_asset', $post_id);
    $assets = array();
    
    if (is_array($ckan_assets) && count($ckan_assets) > 0) {
        foreach ($ckan_assets as $index => $asset) {
            $assets[] = array(
                DGA_NAME_FIELD => isset($asset['ckan_asset_name']) ? $asset['ckan_asset_name'] : '',
                'description' => isset($asset['ckan_asset_discription']) ? $asset['ckan_asset_discription'] : '',
                'file_url' => isset($asset['ckan_asset_link']) ? $asset['ckan_asset_link'] : '',
                'file_ext' => pathinfo(isset($asset['ckan_asset_link']) ? $asset['ckan_asset_link'] : '', PATHINFO_EXTENSION),
                'file_index' => $index,
                'file_api_url' => home_url('/wp-json/ckan/v1/file/' . $post_id . '/' . $index)
            );
        }
    }
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á object ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏à‡∏∞‡∏™‡πà‡∏á‡∏Å‡∏•‡∏±‡∏ö
    $result = array(
        DGA_SUCCESS_STATUS => true,
        'result' => array(
            'id' => $post_id,
            DGA_TITLE_FIELD => get_the_title($post_id),
            'content' => get_the_content(null, false, $post_id),
            'permalink' => get_permalink($post_id),
            'post_date' => get_the_date('c', $post_id),
            'post_modified' => get_the_modified_date('c', $post_id),
            'metadata' => $metadata,
            'resources' => $assets,
            DGA_TOTAL_FIELD_KEY => count($assets)
        )
    );
    
    // ‡∏≠‡πà‡∏≤‡∏ô‡∏Ñ‡πà‡∏≤ limit ‡πÅ‡∏•‡∏∞ offset ‡∏à‡∏≤‡∏Å query params
    $params = $request->get_params();
    $limit = isset($params['limit']) ? intval($params['limit']) : null;
    $offset = isset($params['offset']) ? intval($params['offset']) : 0;
    
    // ‡∏à‡∏≥‡∏Å‡∏±‡∏î‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏™‡πà‡∏á‡∏Å‡∏•‡∏±‡∏ö
    if ($limit !== null && $limit > 0) {
        $result['result']['resources'] = array_slice($result['result']['resources'], $offset, $limit);
    }
    
    return $result;
}

// ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•
function ckan_search_data($request) {
    // ‡∏£‡∏±‡∏ö‡∏Ñ‡πà‡∏≤‡∏û‡∏≤‡∏£‡∏≤‡∏°‡∏¥‡πÄ‡∏ï‡∏≠‡∏£‡πå‡∏à‡∏≤‡∏Å query
    $params = $request->get_params();
    $query = isset($params['q']) ? sanitize_text_field($params['q']) : '';
    $limit = isset($params['limit']) ? intval($params['limit']) : 10;
    $offset = isset($params['offset']) ? intval($params['offset']) : 0;
    $resource_id = isset($params['resource_id']) ? sanitize_text_field($params['resource_id']) : '';
    
    // ‡∏ñ‡πâ‡∏≤‡∏°‡∏µ resource_id ‡πÅ‡∏™‡∏î‡∏á‡∏ß‡πà‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡πÉ‡∏ô‡πÑ‡∏ü‡∏•‡πå‡πÄ‡∏â‡∏û‡∏≤‡∏∞
    if (!empty($resource_id)) {
        return ckan_search_in_resources($resource_id, $params);
    }
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á query args ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏Å‡∏≤‡∏£‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤
    $args = array(
        DGA_POST_TYPE_FIELD => 'post',  // ‡∏õ‡∏£‡∏±‡∏ö‡πÄ‡∏õ‡πá‡∏ô post type ‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£
        DGA_POSTS_PER_PAGE => $limit,
        'offset' => $offset,
        's' => $query,  // ‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡∏à‡∏≤‡∏Å‡∏Ñ‡∏≥‡∏ó‡∏µ‡πà‡∏Å‡∏≥‡∏´‡∏ô‡∏î
    );
    
    // ‡∏î‡∏≥‡πÄ‡∏ô‡∏¥‡∏ô‡∏Å‡∏≤‡∏£‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤
    $search_query = new WP_Query($args);
    $results = array();
    
    if ($search_query->have_posts()) {
        while ($search_query->have_posts()) {
            $search_query->the_post();
            $post_id = get_the_ID();
            
            // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÑ‡∏ü‡∏•‡πå‡πÅ‡∏ô‡∏ö‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡πÇ‡∏û‡∏™‡∏ï‡πå‡∏ô‡∏µ‡πâ
            $ckan_assets = get_field('ckan_asset', $post_id);
            $assets = array();
            
            if (is_array($ckan_assets) && count($ckan_assets) > 0) {
                foreach ($ckan_assets as $index => $asset) {
                    $assets[] = array(
                        DGA_NAME_FIELD => isset($asset['ckan_asset_name']) ? $asset['ckan_asset_name'] : '',
                        'description' => isset($asset['ckan_asset_discription']) ? $asset['ckan_asset_discription'] : '',
                        'file_url' => isset($asset['ckan_asset_link']) ? $asset['ckan_asset_link'] : '',
                        'file_ext' => pathinfo(isset($asset['ckan_asset_link']) ? $asset['ckan_asset_link'] : '', PATHINFO_EXTENSION),
                        'file_index' => $index,
                        'file_api_url' => home_url('/wp-json/ckan/v1/file/' . $post_id . '/' . $index)
                    );
                }
            }
            
            // ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÇ‡∏û‡∏™‡∏ï‡πå‡πÄ‡∏Ç‡πâ‡∏≤‡πÉ‡∏ô‡∏ú‡∏•‡∏•‡∏±‡∏û‡∏ò‡πå
            $results[] = array(
                'id' => $post_id,
                DGA_TITLE_FIELD => get_the_title(),
                'excerpt' => get_the_excerpt(),
                'permalink' => get_permalink(),
                'post_date' => get_the_date('c'),
                'resources' => $assets,
                'resource_count' => count($assets)
            );
        }
        
        wp_reset_postdata();
    }
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á object ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏à‡∏∞‡∏™‡πà‡∏á‡∏Å‡∏•‡∏±‡∏ö
    $result = array(
        DGA_SUCCESS_STATUS => true,
        'result' => array(
            'count' => $search_query->found_posts,
            'items' => $results,
            'limit' => $limit,
            'offset' => $offset,
            'query' => $query
        )
    );
    
    return $result;
}

// ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡πÉ‡∏ô‡πÑ‡∏ü‡∏•‡πå‡∏ó‡∏£‡∏±‡∏û‡∏¢‡∏≤‡∏Å‡∏£
function ckan_search_in_resources($resource_id, $params) {
    $q = isset($params['q']) ? sanitize_text_field($params['q']) : '';
    $limit = isset($params['limit']) ? intval($params['limit']) : 10;
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏≥‡∏•‡∏≠‡∏á‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏Å‡∏≤‡∏£‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡πÉ‡∏ô‡πÑ‡∏ü‡∏•‡πå
    // ‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô‡∏à‡∏£‡∏¥‡∏á ‡∏ô‡∏µ‡πà‡∏Ñ‡∏∑‡∏≠‡∏ó‡∏µ‡πà‡∏Ñ‡∏∏‡∏ì‡∏à‡∏∞‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏≤‡∏Å‡πÑ‡∏ü‡∏•‡πå‡πÅ‡∏•‡∏∞‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡πÉ‡∏ô‡∏ô‡∏±‡πâ‡∏ô
    
    // ‡πÉ‡∏ô‡∏ï‡∏±‡∏ß‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏ô‡∏µ‡πâ‡πÄ‡∏£‡∏≤‡πÅ‡∏Ñ‡πà‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏≥‡∏•‡∏≠‡∏á
    $mock_data = array();
    for ($i = 1; $i <= min(30, $limit); $i++) {
        $record = array(
            'id' => $i,
            DGA_NAME_FIELD => 'Record ' . $i,
            'description' => 'Description for record ' . $i,
            'value' => rand(100, 10000) / 100
        );
        
        // ‡∏ñ‡πâ‡∏≤‡∏°‡∏µ‡∏Å‡∏≤‡∏£‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤ ‡πÉ‡∏´‡πâ‡πÅ‡∏™‡∏î‡∏á‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏ó‡∏µ‡πà‡∏°‡∏µ‡∏Ñ‡∏≥‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£
        if (!empty($q)) {
            if (stripos($record['name'], $q) !== false || stripos($record['description'], $q) !== false) {
                $mock_data[] = $record;
            } else if ($q == 'jones' && $i % 5 == 0) {  // ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏ï‡∏±‡∏ß‡∏≠‡∏¢‡πà‡∏≤‡∏á jones
                $record['name'] = 'Jones Record ' . $i;
                $mock_data[] = $record;
            }
        } else {
            $mock_data[] = $record;
        }
    }
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á response ‡πÅ‡∏ö‡∏ö‡πÄ‡∏î‡∏µ‡∏¢‡∏ß‡∏Å‡∏±‡∏ö‡∏ó‡∏µ‡πà CKAN API ‡∏™‡πà‡∏á‡∏Å‡∏•‡∏±‡∏ö
    $result = array(
        DGA_SUCCESS_STATUS => true,
        'result' => array(
            'resource_id' => $resource_id,
            DGA_FIELDS_PARAMETER => array(
                array('id' => 'id', DGA_TYPE_FIELD => 'int'),
                array('id' => 'name', DGA_TYPE_FIELD => 'text'),
                array('id' => 'description', DGA_TYPE_FIELD => 'text'),
                array('id' => 'value', DGA_TYPE_FIELD => 'float')
            ),
            'records' => $mock_data,
            'limit' => $limit,
            DGA_TOTAL_FIELD_KEY => count($mock_data)
        )
    );
    
    return $result;
}


// ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏≤‡∏Å‡πÑ‡∏ü‡∏•‡πå‡πÇ‡∏î‡∏¢‡∏ï‡∏£‡∏á
function ckan_get_file_data($request) {
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏°‡∏µ‡∏Å‡∏≤‡∏£‡∏ï‡∏¥‡∏î‡∏ï‡∏±‡πâ‡∏á PhpSpreadsheet ‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
    $has_phpspreadsheet = class_exists('PhpOffice\PhpSpreadsheet\IOFactory');
    
    $post_id = $request[DGA_POST_ID_FIELD];
    $file_index = $request['file_index'];
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡πÇ‡∏û‡∏™‡∏ï‡πå‡∏°‡∏µ‡∏≠‡∏¢‡∏π‡πà‡∏à‡∏£‡∏¥‡∏á
    $post = get_post($post_id);
    if (!$post) {
        return new WP_Error('post_not_found', '‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö ID ‡∏ô‡∏µ‡πâ', array(DGA_STATUS_FIELD => 404));
    }
    
    // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÑ‡∏ü‡∏•‡πå‡πÅ‡∏ô‡∏ö‡∏à‡∏≤‡∏Å Repeater field
    $ckan_assets = get_field('ckan_asset', $post_id);
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏°‡∏µ‡πÑ‡∏ü‡∏•‡πå‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
    if (!is_array($ckan_assets) || !isset($ckan_assets[$file_index])) {
        return new WP_Error('file_not_found', '‡πÑ‡∏°‡πà‡∏û‡∏ö‡πÑ‡∏ü‡∏•‡πå‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£', array(DGA_STATUS_FIELD => 404));
    }
    
    $asset = $ckan_assets[$file_index];
    $file_url = isset($asset['ckan_asset_link']) ? $asset['ckan_asset_link'] : '';
    $file_name = isset($asset['ckan_asset_name']) ? $asset['ckan_asset_name'] : '';
    $file_ext = strtolower(pathinfo($file_url, PATHINFO_EXTENSION));
    
    // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÑ‡∏ü‡∏•‡πå‡πÇ‡∏î‡∏¢‡πÉ‡∏ä‡πâ WordPress HTTP API
    $response = wp_remote_get($file_url, array(
        'timeout' => 60,
        'sslverify' => false
    ));
    
    if (is_wp_error($response)) {
        return new WP_Error('file_fetch_error', $response->get_error_message(), array(DGA_STATUS_FIELD => 500));
    }
    
    $file_content = wp_remote_retrieve_body($response);
    if (empty($file_content)) {
        return new WP_Error('empty_file', '‡πÑ‡∏ü‡∏•‡πå‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•', array(DGA_STATUS_FIELD => 404));
    }
    
    // ‡πÅ‡∏õ‡∏•‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÑ‡∏ü‡∏•‡πå‡∏ï‡∏≤‡∏°‡∏ô‡∏≤‡∏°‡∏™‡∏Å‡∏∏‡∏•
    $data = array();
    
    switch ($file_ext) {
        case 'csv':
            $data = ckan_parse_csv_to_array($file_content);
            break;
        
        case 'json':
            $json_data = json_decode($file_content, true);
            if (json_last_error() !== JSON_ERROR_NONE) {
                return new WP_Error('json_parse_error', '‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡πÅ‡∏õ‡∏•‡∏á‡πÑ‡∏ü‡∏•‡πå JSON ‡πÑ‡∏î‡πâ', array(DGA_STATUS_FIELD => 500));
            }
            $data = ckan_format_json_data($json_data);
            break;
            
        case 'xlsx':
        case 'xls':
            if (!$has_phpspreadsheet) {
                // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏°‡∏µ PhpSpreadsheet ‡πÉ‡∏ä‡πâ‡∏ß‡∏¥‡∏ò‡∏µ fallback ‡∏ñ‡πâ‡∏≤‡πÄ‡∏õ‡πá‡∏ô‡πÑ‡∏ü‡∏•‡πå Excel
                $temp_file = wp_tempnam($file_name);
                file_put_contents($temp_file, $file_content);
                $data = ckan_parse_excel_fallback($temp_file, $file_ext);
                @unlink($temp_file); // ‡∏•‡∏ö‡πÑ‡∏ü‡∏•‡πå‡∏ä‡∏±‡πà‡∏ß‡∏Ñ‡∏£‡∏≤‡∏ß
            } else {
                // ‡∏ñ‡πâ‡∏≤‡∏°‡∏µ PhpSpreadsheet ‡πÉ‡∏ä‡πâ‡πÑ‡∏•‡∏ö‡∏£‡∏≤‡∏£‡∏µ‡∏ô‡∏µ‡πâ‡∏≠‡πà‡∏≤‡∏ô‡πÑ‡∏ü‡∏•‡πå Excel
                $temp_file = wp_tempnam($file_name);
                file_put_contents($temp_file, $file_content);
                $data = ckan_parse_excel_with_phpspreadsheet($temp_file, $file_ext);
                @unlink($temp_file); // ‡∏•‡∏ö‡πÑ‡∏ü‡∏•‡πå‡∏ä‡∏±‡πà‡∏ß‡∏Ñ‡∏£‡∏≤‡∏ß
            }
            break;
            
        case 'txt':
        case 'text':
        case 'md':
            // ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÑ‡∏ü‡∏•‡πå‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏° ‡πÅ‡∏õ‡∏•‡∏á‡πÄ‡∏õ‡πá‡∏ô JSON ‡πÅ‡∏ö‡∏ö‡∏û‡∏∑‡πâ‡∏ô‡∏ê‡∏≤‡∏ô
            $data = ckan_format_text_data($file_content);
            break;
            
        default:
            // ‡πÑ‡∏ü‡∏•‡πå‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏≠‡∏∑‡πà‡∏ô‡πÜ ‡∏ó‡∏µ‡πà‡πÑ‡∏°‡πà‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö
            return new WP_Error('format_not_supported', '‡πÑ‡∏°‡πà‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö‡πÑ‡∏ü‡∏•‡πå‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó ' . $file_ext, array(DGA_STATUS_FIELD => 400));
    }
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á response ‡∏ï‡∏≤‡∏°‡∏£‡∏π‡∏õ‡πÅ‡∏ö‡∏ö‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£
    $result = array(
        DGA_SUCCESS_STATUS => true,
        'result' => array(
            'resource_id' => 'b8a8a6b5-' . substr(md5($post_id . '_' . $file_index), 0, 4) . '-' . 
                            substr(md5($post_id . '_' . $file_index), 4, 4) . '-' . 
                            substr(md5($post_id . '_' . $file_index), 8, 4) . '-' . 
                            substr(md5($post_id . '_' . $file_index), 12, 12),
            DGA_FIELDS_PARAMETER => $data[DGA_FIELDS_PARAMETER],
            'records' => $data['records'],
            'limit' => count($data['records']),
            DGA_TOTAL_FIELD_KEY => count($data['records'])
        )
    );
    
    // ‡∏Å‡∏£‡∏ì‡∏µ‡∏°‡∏µ‡∏Å‡∏≤‡∏£‡∏à‡∏≥‡∏Å‡∏±‡∏î‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•
    $params = $request->get_params();
    $limit = isset($params['limit']) ? intval($params['limit']) : count($data['records']);
    
    if ($limit > 0 && $limit < count($data['records'])) {
        $result['result']['records'] = array_slice($data['records'], 0, $limit);
        $result['result']['limit'] = $limit;
    }
    
    return $result;
}


/**
 * ‡πÅ‡∏õ‡∏•‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏• CSV ‡πÄ‡∏õ‡πá‡∏ô Array
 */
function ckan_parse_csv_to_array($csv_content) {
    $lines = explode("\n", $csv_content);
    $data = array(
        DGA_FIELDS_PARAMETER => array(),
        'records' => array()
    );
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏°‡∏µ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
    if (count($lines) < 2) {
        return $data;
    }
    
    // ‡πÅ‡∏¢‡∏Å‡∏™‡πà‡∏ß‡∏ô‡∏´‡∏±‡∏ß (header)
    $header = str_getcsv($lines[0]);
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á fields metadata
    foreach ($header as $field_name) {
        $field_name = trim($field_name);
        $field_type = 'text'; // ‡∏Ñ‡πà‡∏≤‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô‡πÄ‡∏õ‡πá‡∏ô text
        
        // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤ field ‡∏ô‡∏µ‡πâ‡∏ô‡πà‡∏≤‡∏à‡∏∞‡πÄ‡∏õ‡πá‡∏ô‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏î (‡πÄ‡∏ä‡πá‡∏Ñ‡∏à‡∏≤‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏ô‡πÑ‡∏ü‡∏•‡πå)
        for ($i = 1; $i < min(count($lines), 5); $i++) {
            if (empty(trim($lines[$i]))) continue;
            
            $row = str_getcsv($lines[$i]);
            $index = array_search($field_name, $header);
            
            if ($index !== false && isset($row[$index])) {
                $value = trim($row[$index]);
                
                // ‡∏ñ‡πâ‡∏≤‡πÄ‡∏õ‡πá‡∏ô‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏Ç‡∏•‡πâ‡∏ß‡∏ô ‡πÄ‡∏õ‡πá‡∏ô int
                if (is_numeric($value) && strpos($value, '.') === false) {
                    $field_type = 'int';
                }
                // ‡∏ñ‡πâ‡∏≤‡πÄ‡∏õ‡πá‡∏ô‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏Ç‡∏ó‡∏®‡∏ô‡∏¥‡∏¢‡∏° ‡πÄ‡∏õ‡πá‡∏ô float
                else if (is_numeric($value)) {
                    $field_type = 'float';
                }
            }
        }
        
        $data[DGA_FIELDS_PARAMETER][] = array(
            'id' => $field_name,
            DGA_TYPE_FIELD => $field_type
        );
    }
    
    // ‡πÅ‡∏õ‡∏•‡∏á‡πÅ‡∏ï‡πà‡∏•‡∏∞‡∏ö‡∏£‡∏£‡∏ó‡∏±‡∏î‡πÄ‡∏õ‡πá‡∏ô‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•
    for ($i = 1; $i < count($lines); $i++) {
        if (empty(trim($lines[$i]))) continue; // ‡∏Ç‡πâ‡∏≤‡∏°‡∏ö‡∏£‡∏£‡∏ó‡∏±‡∏î‡∏ß‡πà‡∏≤‡∏á
        
        $row = str_getcsv($lines[$i]);
        $item = array();
        
        for ($j = 0; $j < count($header); $j++) {
            $field_name = trim($header[$j]);
            
            // ‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤ field type
            $field_type = 'text';
            foreach ($data[DGA_FIELDS_PARAMETER] as $field) {
                if ($field['id'] === $field_name) {
                    $field_type = $field['type'];
                    break;
                }
            }
            
            if (isset($row[$j])) {
                $value = trim($row[$j]);
                
                // ‡πÅ‡∏õ‡∏•‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ï‡∏≤‡∏° type
                if ($field_type === 'int' && is_numeric($value)) {
                    $item[$field_name] = intval($value);
                }
                else if ($field_type === 'float' && is_numeric($value)) {
                    $item[$field_name] = floatval($value);
                }
                else {
                    $item[$field_name] = $value;
                }
            } else {
                $item[$field_name] = ''; // ‡∏´‡∏≤‡∏Å‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏Ñ‡πà‡∏≤‡πÉ‡∏´‡πâ‡πÄ‡∏õ‡πá‡∏ô‡∏Ñ‡πà‡∏≤‡∏ß‡πà‡∏≤‡∏á
            }
        }
        
        $data['records'][] = $item;
    }
    
    return $data;
}

/**
 * ‡πÅ‡∏õ‡∏•‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏• Excel ‡πÅ‡∏ö‡∏ö fallback ‡πÇ‡∏î‡∏¢‡πÉ‡∏ä‡πâ SheetJS ‡πÉ‡∏ô JavaScript
 * (‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏Å‡∏£‡∏ì‡∏µ‡∏ó‡∏µ‡πà‡πÑ‡∏°‡πà‡∏°‡∏µ PhpSpreadsheet)
 */
function ckan_parse_excel_fallback($file_path, $file_ext) {
    // ‡πÇ‡∏Ñ‡πâ‡∏î‡∏ô‡∏µ‡πâ‡∏à‡∏≥‡πÄ‡∏õ‡πá‡∏ô‡∏ï‡πâ‡∏≠‡∏á‡πÉ‡∏ä‡πâ SheetJS ‡πÉ‡∏ô‡πÑ‡∏ü‡∏•‡πå JS
    // ‡πÅ‡∏ï‡πà‡πÄ‡∏£‡∏≤‡∏ó‡∏≥‡πÑ‡∏î‡πâ‡πÅ‡∏Ñ‡πà‡∏™‡πà‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏î‡∏¥‡∏ö‡∏Å‡∏•‡∏±‡∏ö‡πÑ‡∏õ‡πÅ‡∏•‡πâ‡∏ß‡πÅ‡∏õ‡∏•‡∏á‡πÄ‡∏õ‡πá‡∏ô base64 ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏™‡πà‡∏á‡πÑ‡∏õ‡πÉ‡∏´‡πâ JS ‡πÅ‡∏õ‡∏•‡∏á
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á‡πÇ‡∏Ñ‡∏£‡∏á‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÄ‡∏õ‡∏•‡πà‡∏≤
    $data = array(
        DGA_FIELDS_PARAMETER => array(
            array('id' => 'id', DGA_TYPE_FIELD => 'int'),
            array('id' => 'name', DGA_TYPE_FIELD => 'text'),
            array('id' => 'description', DGA_TYPE_FIELD => 'text'),
            array('id' => 'value', DGA_TYPE_FIELD => 'float')
        ),
        'records' => array()
    );
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ï‡∏±‡∏ß‡∏≠‡∏¢‡πà‡∏≤‡∏á (‡πÉ‡∏ô‡∏Å‡∏£‡∏ì‡∏µ‡∏ó‡∏µ‡πà‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏≠‡πà‡∏≤‡∏ô‡πÑ‡∏ü‡∏•‡πå Excel ‡πÑ‡∏î‡πâ)
    for ($i = 1; $i <= 5; $i++) {
        $data['records'][] = array(
            'id' => $i,
            DGA_NAME_FIELD => 'Record ' . $i,
            'description' => 'Description for record ' . $i,
            'value' => rand(5000, 10000) / 100
        );
    }
    
    return $data;
}



/**
 * ‡πÅ‡∏õ‡∏•‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏• Excel ‡πÇ‡∏î‡∏¢‡πÉ‡∏ä‡πâ PhpSpreadsheet
 */
function ckan_parse_excel_with_phpspreadsheet($file_path, $file_ext) {
    // ‡∏ï‡πâ‡∏≠‡∏á‡∏°‡∏µ‡∏Å‡∏≤‡∏£ include PhpSpreadsheet ‡∏Å‡πà‡∏≠‡∏ô
    // require_once 'vendor/autoload.php';
    
    try {
        $spreadsheet = \PhpOffice\PhpSpreadsheet\IOFactory::load($file_path);
        $worksheet = $spreadsheet->getActiveSheet();
        $data = array(
            DGA_FIELDS_PARAMETER => array(),
            'records' => array()
        );
        
        // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÄ‡∏õ‡πá‡∏ô array
        $rows = $worksheet->toArray();
        
        // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•
        if (empty($rows)) {
            return $data;
        }
        
        // ‡πÅ‡∏¢‡∏Å‡∏™‡πà‡∏ß‡∏ô‡∏´‡∏±‡∏ß (header)
        $header = $rows[0];
        
        // ‡∏™‡∏£‡πâ‡∏≤‡∏á fields metadata
        foreach ($header as $field_name) {
            if (empty($field_name)) continue;
            
            $field_type = 'text'; // ‡∏Ñ‡πà‡∏≤‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô‡πÄ‡∏õ‡πá‡∏ô text
            
            // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤ field ‡∏ô‡∏µ‡πâ‡∏ô‡πà‡∏≤‡∏à‡∏∞‡πÄ‡∏õ‡πá‡∏ô‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏î (‡πÄ‡∏ä‡πá‡∏Ñ‡∏à‡∏≤‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏ô‡πÑ‡∏ü‡∏•‡πå)
            for ($i = 1; $i < min(count($rows), 5); $i++) {
                $index = array_search($field_name, $header);
                
                if ($index !== false && isset($rows[$i][$index])) {
                    $value = $rows[$i][$index];
                    
                    // ‡∏ñ‡πâ‡∏≤‡πÄ‡∏õ‡πá‡∏ô‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏Ç‡∏•‡πâ‡∏ß‡∏ô ‡πÄ‡∏õ‡πá‡∏ô int
                    if (is_int($value) || (is_string($value) && ctype_digit($value))) {
                        $field_type = 'int';
                    }
                    // ‡∏ñ‡πâ‡∏≤‡πÄ‡∏õ‡πá‡∏ô‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏Ç‡∏ó‡∏®‡∏ô‡∏¥‡∏¢‡∏° ‡πÄ‡∏õ‡πá‡∏ô float
                    else if (is_float($value) || (is_string($value) && is_numeric($value) && strpos($value, '.') !== false)) {
                        $field_type = 'float';
                    }
                }
            }
            
            $data[DGA_FIELDS_PARAMETER][] = array(
                'id' => $field_name,
                DGA_TYPE_FIELD => $field_type
            );
        }
        
        // ‡πÅ‡∏õ‡∏•‡∏á‡πÅ‡∏ï‡πà‡∏•‡∏∞‡πÅ‡∏ñ‡∏ß‡πÄ‡∏õ‡πá‡∏ô‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•
        for ($i = 1; $i < count($rows); $i++) {
            $row = $rows[$i];
            $item = array();
            
            foreach ($data[DGA_FIELDS_PARAMETER] as $j => $field) {
                $field_name = $field['id'];
                $field_type = $field['type'];
                
                if (isset($row[$j])) {
                    $value = $row[$j];
                    
                    // ‡πÅ‡∏õ‡∏•‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ï‡∏≤‡∏° type
                    if ($field_type === 'int' && (is_int($value) || (is_string($value) && ctype_digit($value)))) {
                        $item[$field_name] = intval($value);
                    }
                    else if ($field_type === 'float' && (is_float($value) || (is_string($value) && is_numeric($value)))) {
                        $item[$field_name] = floatval($value);
                    }
                    else {
                        $item[$field_name] = (string)$value;
                    }
                } else {
                    $item[$field_name] = ''; // ‡∏´‡∏≤‡∏Å‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏Ñ‡πà‡∏≤‡πÉ‡∏´‡πâ‡πÄ‡∏õ‡πá‡∏ô‡∏Ñ‡πà‡∏≤‡∏ß‡πà‡∏≤‡∏á
                }
            }
            
            $data['records'][] = $item;
        }
        
        return $data;
    } catch (\Exception $e) {
        // ‡∏Å‡∏£‡∏ì‡∏µ‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î ‡πÉ‡∏ä‡πâ‡∏ß‡∏¥‡∏ò‡∏µ fallback
        return ckan_parse_excel_fallback($file_path, $file_ext);
    }
}


/**
 * ‡πÅ‡∏õ‡∏•‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏• JSON ‡πÄ‡∏õ‡πá‡∏ô‡∏£‡∏π‡∏õ‡πÅ‡∏ö‡∏ö‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£
 */
function ckan_format_json_data($json_data) {
    $data = array(
        DGA_FIELDS_PARAMETER => array(),
        'records' => array()
    );
    
    // ‡∏ñ‡πâ‡∏≤ JSON ‡∏°‡∏µ‡πÇ‡∏Ñ‡∏£‡∏á‡∏™‡∏£‡πâ‡∏≤‡∏á‡πÄ‡∏õ‡πá‡∏ô array ‡∏Ç‡∏≠‡∏á object
    if (is_array($json_data) && !empty($json_data) && is_array($json_data[0])) {
        // ‡πÉ‡∏ä‡πâ key ‡∏Ç‡∏≠‡∏á object ‡πÅ‡∏£‡∏Å‡πÄ‡∏õ‡πá‡∏ô fields
        $first_object = $json_data[0];
        
        foreach ($first_object as $key => $value) {
            $type = 'text';
            
            if (is_int($value)) {
                $type = 'int';
            } else if (is_float($value) || (is_string($value) && is_numeric($value) && strpos($value, '.') !== false)) {
                $type = 'float';
            }
            
            $data[DGA_FIELDS_PARAMETER][] = array(
                'id' => $key,
                DGA_TYPE_FIELD => $type
            );
        }
        
        // ‡πÉ‡∏ä‡πâ‡∏ó‡∏∏‡∏Å object ‡πÄ‡∏õ‡πá‡∏ô records
        $data['records'] = $json_data;
    }
    // ‡∏ñ‡πâ‡∏≤ JSON ‡∏°‡∏µ‡πÇ‡∏Ñ‡∏£‡∏á‡∏™‡∏£‡πâ‡∏≤‡∏á‡πÄ‡∏õ‡πá‡∏ô object ‡∏ó‡∏µ‡πà‡∏°‡∏µ property ‡πÄ‡∏õ‡πá‡∏ô array
    else if (is_array($json_data)) {
        // ‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤ property ‡∏ó‡∏µ‡πà‡πÄ‡∏õ‡πá‡∏ô array
        $array_properties = array();
        
        foreach ($json_data as $key => $value) {
            if (is_array($value) && !empty($value) && is_array($value[0])) {
                $array_properties[$key] = $value;
            }
        }
        
        // ‡∏ñ‡πâ‡∏≤‡∏°‡∏µ property ‡∏ó‡∏µ‡πà‡πÄ‡∏õ‡πá‡∏ô array
        if (!empty($array_properties)) {
            // ‡πÉ‡∏ä‡πâ array ‡πÅ‡∏£‡∏Å‡∏ó‡∏µ‡πà‡∏û‡∏ö
            $first_array_key = array_key_first($array_properties);
            $first_array = $array_properties[$first_array_key];
            
            if (!empty($first_array) && is_array($first_array[0])) {
                // ‡πÉ‡∏ä‡πâ key ‡∏Ç‡∏≠‡∏á object ‡πÅ‡∏£‡∏Å‡πÄ‡∏õ‡πá‡∏ô fields
                $first_object = $first_array[0];
                
                foreach ($first_object as $key => $value) {
                    $type = 'text';
                    
                    if (is_int($value)) {
                        $type = 'int';
                    } else if (is_float($value) || (is_string($value) && is_numeric($value) && strpos($value, '.') !== false)) {
                        $type = 'float';
                    }
                    
                    $data[DGA_FIELDS_PARAMETER][] = array(
                        'id' => $key,
                        DGA_TYPE_FIELD => $type
                    );
                }
                
                // ‡πÉ‡∏ä‡πâ‡∏ó‡∏∏‡∏Å object ‡πÉ‡∏ô array ‡πÄ‡∏õ‡πá‡∏ô records
                $data['records'] = $first_array;
            }
        }
        // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏°‡∏µ property ‡∏ó‡∏µ‡πà‡πÄ‡∏õ‡πá‡∏ô array ‡πÅ‡∏ï‡πà JSON ‡πÄ‡∏õ‡πá‡∏ô object
        else {
            // ‡πÅ‡∏õ‡∏•‡∏á object ‡πÄ‡∏õ‡πá‡∏ô‡πÅ‡∏ñ‡∏ß‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÄ‡∏î‡∏µ‡∏¢‡∏ß
            $fields = array();
            $record = array();
            
            foreach ($json_data as $key => $value) {
                $type = 'text';
                
                if (is_int($value)) {
                    $type = 'int';
                    $record[$key] = $value;
                } else if (is_float($value) || (is_string($value) && is_numeric($value) && strpos($value, '.') !== false)) {
                    $type = 'float';
                    $record[$key] = (float)$value;
                } else if (is_bool($value)) {
                    $type = 'boolean';
                    $record[$key] = $value ? 'true' : 'false';
                } else if (is_string($value)) {
                    $record[$key] = $value;
                } else {
                    // ‡∏ñ‡πâ‡∏≤‡πÄ‡∏õ‡πá‡∏ô object ‡∏´‡∏£‡∏∑‡∏≠ array ‡∏ã‡πâ‡∏≠‡∏ô‡∏Å‡∏±‡∏ô ‡πÅ‡∏õ‡∏•‡∏á‡πÄ‡∏õ‡πá‡∏ô JSON string
                    $record[$key] = json_encode($value);
                }
                
                $fields[] = array(
                    'id' => $key,
                    DGA_TYPE_FIELD => $type
                );
            }
            
            $data[DGA_FIELDS_PARAMETER] = $fields;
            $data['records'] = array($record);
        }
    }
    
    return $data;
}


/**
 * ‡πÅ‡∏õ‡∏•‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏≤‡∏Å‡πÑ‡∏ü‡∏•‡πå‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏°‡∏ò‡∏£‡∏£‡∏°‡∏î‡∏≤‡πÄ‡∏õ‡πá‡∏ô JSON
 */
function ckan_format_text_data($text_content) {
    $data = array(
        DGA_FIELDS_PARAMETER => array(
            array('id' => 'line', DGA_TYPE_FIELD => 'int'),
            array('id' => 'content', DGA_TYPE_FIELD => 'text')
        ),
        'records' => array()
    );
    
    // ‡πÅ‡∏ö‡πà‡∏á‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏°‡πÄ‡∏õ‡πá‡∏ô‡∏ö‡∏£‡∏£‡∏ó‡∏±‡∏î
    $lines = explode("\n", $text_content);
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÅ‡∏ï‡πà‡∏•‡∏∞‡∏ö‡∏£‡∏£‡∏ó‡∏±‡∏î
    foreach ($lines as $i => $line) {
        $data['records'][] = array(
            'line' => $i + 1,
            'content' => $line
        );
    }
    
    return $data;
}


// ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡πÅ‡∏õ‡∏•‡∏á CSV ‡πÄ‡∏õ‡πá‡∏ô JSON
function ckan_parse_csv_to_json($csv_content, $title = '') {
    $lines = explode("\n", $csv_content);
    $data = array();
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏°‡∏µ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
    if (count($lines) < 2) {
        return array();
    }
    
    // ‡πÅ‡∏¢‡∏Å‡∏™‡πà‡∏ß‡∏ô‡∏´‡∏±‡∏ß (header)
    $header = str_getcsv($lines[0]);
    
    // ‡πÅ‡∏õ‡∏•‡∏á‡πÅ‡∏ï‡πà‡∏•‡∏∞‡∏ö‡∏£‡∏£‡∏ó‡∏±‡∏î‡πÄ‡∏õ‡πá‡∏ô JSON
    for ($i = 1; $i < count($lines); $i++) {
        if (empty(trim($lines[$i]))) continue; // ‡∏Ç‡πâ‡∏≤‡∏°‡∏ö‡∏£‡∏£‡∏ó‡∏±‡∏î‡∏ß‡πà‡∏≤‡∏á
        
        $row = str_getcsv($lines[$i]);
        $item = array();
        
        for ($j = 0; $j < count($header); $j++) {
            if (isset($row[$j])) {
                $item[$header[$j]] = $row[$j];
            } else {
                $item[$header[$j]] = ''; // ‡∏´‡∏≤‡∏Å‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏Ñ‡πà‡∏≤‡πÉ‡∏´‡πâ‡πÄ‡∏õ‡πá‡∏ô‡∏Ñ‡πà‡∏≤‡∏ß‡πà‡∏≤‡∏á
            }
        }
        
        $data[] = $item;
    }
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á‡πÇ‡∏Ñ‡∏£‡∏á‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÄ‡∏´‡∏°‡∏∑‡∏≠‡∏ô‡∏Å‡∏±‡∏ö CKAN
    $result = array(
        DGA_TITLE_FIELD => $title,
        DGA_FIELDS_PARAMETER => array(),
        'records' => $data
    );
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á fields metadata
    foreach ($header as $field_name) {
        $result[DGA_FIELDS_PARAMETER][] = array(
            'id' => $field_name,
            DGA_TYPE_FIELD => 'text' // ‡∏Ñ‡πà‡∏≤‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô‡πÄ‡∏õ‡πá‡∏ô text
        );
    }
    
    return $result;
}

// ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏´‡∏°‡πà (‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏ï‡∏±‡∏ß‡∏≠‡∏¢‡πà‡∏≤‡∏á‡πÄ‡∏ó‡πà‡∏≤‡∏ô‡∏±‡πâ‡∏ô)
function ckan_create_data($request) {
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå
    if (!current_user_can(DGA_EDIT_POSTS_CAP)) {
        return new WP_Error('permission_denied', '‡∏Ñ‡∏∏‡∏ì‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•', array(DGA_STATUS_FIELD => 403));
    }
    
    // ‡∏£‡∏±‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏≤‡∏Å request
    $parameters = $request->get_params();
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏à‡∏≥‡πÄ‡∏õ‡πá‡∏ô
    if (empty($parameters['resource_data'])) {
        return new WP_Error('missing_data', '‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏à‡∏≥‡πÄ‡∏õ‡πá‡∏ô', array(DGA_STATUS_FIELD => 400));
    }
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á response
    $result = array(
        DGA_SUCCESS_STATUS => true,
        'result' => array(
            DGA_MESSAGE_KEY => '‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à',
            'resource_id' => 'new_' . time(),
            'data' => $parameters['resource_data']
        )
    );
    
    return $result;
}

// ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏≠‡∏±‡∏û‡πÄ‡∏î‡∏ó‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏• (‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏ï‡∏±‡∏ß‡∏≠‡∏¢‡πà‡∏≤‡∏á‡πÄ‡∏ó‡πà‡∏≤‡∏ô‡∏±‡πâ‡∏ô)
function ckan_upsert_data($request) {
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå
    if (!current_user_can(DGA_EDIT_POSTS_CAP)) {
        return new WP_Error('permission_denied', '‡∏Ñ‡∏∏‡∏ì‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏≠‡∏±‡∏û‡πÄ‡∏î‡∏ó‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•', array(DGA_STATUS_FIELD => 403));
    }
    
    // ‡∏£‡∏±‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏≤‡∏Å request
    $parameters = $request->get_params();
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏à‡∏≥‡πÄ‡∏õ‡πá‡∏ô
    if (empty($parameters['resource_id']) || empty($parameters['records'])) {
        return new WP_Error('missing_data', '‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏à‡∏≥‡πÄ‡∏õ‡πá‡∏ô', array(DGA_STATUS_FIELD => 400));
    }
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á response
    $result = array(
        DGA_SUCCESS_STATUS => true,
        'result' => array(
            DGA_MESSAGE_KEY => '‡∏≠‡∏±‡∏û‡πÄ‡∏î‡∏ó‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à',
            'resource_id' => $parameters['resource_id'],
            'records_updated' => count($parameters['records'])
        )
    );
    
    return $result;
}


