// 9. Helper Function: ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤ post ‡πÄ‡∏õ‡πá‡∏ô active ‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
function at_is_post_active_krt456($post_id = null) {
    if (!$post_id) {
        $post_id = get_the_ID();
    }
    
    $status = get_post_meta($post_id, 'at_status', true);
    
    // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏°‡∏µ meta key ‡∏ñ‡∏∑‡∏≠‡∏ß‡πà‡∏≤‡πÄ‡∏õ‡πá‡∏ô active
    return empty($status) || $status === 'active';
}

