        DGA_POSTS_PER_PAGE => $atts[DGA_POSTS_PER_PAGE],
        DGA_ORDERBY_FIELD_VALUE => $atts[DGA_ORDERBY_FIELD_VALUE],
        DGA_ORDER_FIELD => $atts[DGA_ORDER_FIELD],
        'meta_query' => array(
            'relation' => 'OR',
            array(
                'key' => 'at_status',
