            )
        )
    );
    
    $query = new WP_Query($args);
    
    ob_start();
    
    if ($query->have_posts()) {
        echo '<div class="at-active-posts-list">';
        while ($query->have_posts()) {
            $query->the_post();
            ?>
            <article class="at-post-item">
                <h3><a href="<?php the_permalink(); ?>"><?php the_title(); ?></a></h3>
                <div class="at-post-meta">
