        <p><strong>Can Access:</strong> 
            <?php echo $can_access ? '<span style="color: green;">‚úÖ YES</span>' : '<span style="color: red;">‚ùå NO</span>'; ?>
        </p>
        <?php if (current_user_can(DGA_MANAGE_OPTIONS_CAP)): ?>
            <details>
                <summary>Debug Details</summary>
                <pre><?php print_r($debug); ?></pre>
            </details>
        <?php endif; ?>
    </div>
    <?php
    return ob_get_clean();
});

