/**
 * Shortcode ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÅ‡∏™‡∏î‡∏á‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î‡∏Ç‡∏≠‡∏á taxonomy 'corg'
 * 
 * @return string HTML output ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÅ‡∏™‡∏î‡∏á‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£ taxonomy
 */
function ckan_taxo_orglist_shortcode() {
    // ‡πÄ‡∏£‡∏¥‡πà‡∏° output buffering
    ob_start();
    
    // ‡∏î‡∏∂‡∏á‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£ term ‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î‡∏à‡∏≤‡∏Å taxonomy 'corg' ‡∏£‡∏ß‡∏°‡∏ñ‡∏∂‡∏á‡∏ó‡∏µ‡πà‡∏°‡∏µ‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡πÇ‡∏û‡∏™‡∏ï‡πå‡πÄ‡∏õ‡πá‡∏ô 0
    $terms = get_terms(array(
        DGA_TAXONOMY_FIELD => 'corg',
        DGA_HIDE_EMPTY_FIELD => false,
    ));
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏°‡∏µ terms ‡∏≠‡∏¢‡∏π‡πà‡πÅ‡∏•‡∏∞‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î
    if (!is_wp_error($terms) && !empty($terms)) {
        ?>
        <div class="ckan-taxo-container">
            <div class="ckan-taxo-header">
                <h3 class="ckan-taxo-title">Organizations</h3>
            </div>
            <div class="ckan-taxo-content">
                <ul class="ckan-taxo-list">
                    <?php foreach ($terms as $term) : 
                        // ‡∏ô‡∏±‡∏ö‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡πÇ‡∏û‡∏™‡∏ï‡πå‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö term ‡∏ô‡∏µ‡πâ
                        $post_count = $term->count;
                        
                        // ‡∏£‡∏±‡∏ö‡∏•‡∏¥‡∏á‡∏Å‡πå‡∏Ç‡∏≠‡∏á term
                        $term_link = get_term_link($term);
                        
                        // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏•‡∏¥‡∏á‡∏Å‡πå‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á
                        if (!is_wp_error($term_link)) :
                    ?>
                        <li class="ckan-taxo-item">
                            <a href="<?php echo esc_url($term_link); ?>" class="ckan-taxo-item-link">
                                <span class="ckan-taxo-item-name"><?php echo esc_html($term->name); ?></span>
                                <span class="ckan-taxo-item-count"><?php echo esc_html($post_count); ?></span>
                            </a>
                        </li>
                    <?php 
                        endif;
                    endforeach; ?>
                </ul>
            </div>
        </div>
        <?php
    } else {
        // ‡πÅ‡∏™‡∏î‡∏á‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏°‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏û‡∏ö terms
        ?>
        <div class="ckan-taxo-error">
            ‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏≠‡∏á‡∏Ñ‡πå‡∏Å‡∏£
        </div>
        <?php
    }
    
    // ‡∏£‡∏±‡∏ö‡∏Ñ‡πà‡∏≤ output ‡πÅ‡∏•‡∏∞‡∏à‡∏ö‡∏Å‡∏≤‡∏£ buffering
    $output = ob_get_clean();
    
    return $output;
}

