/**
 * AJAX handler for status change
 */
function ckan_post_status_pst638_ajax_handler() {
    // Verify nonce
    if (!check_ajax_referer('ckan_status_pst638_nonce', 'nonce', false)) {
        wp_send_json_error(array(
            DGA_MESSAGE_KEY => __('‡∏Å‡∏≤‡∏£‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ñ‡∏ß‡∏≤‡∏°‡∏õ‡∏•‡∏≠‡∏î‡∏†‡∏±‡∏¢‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß', DGA_TEXT_DOMAIN)
        ), 403);
    }
    
    // Get inputs
    $post_id = isset($_POST[DGA_POST_ID_FIELD]) ? absint($_POST[DGA_POST_ID_FIELD]) : 0;
    $make_public = isset($_POST['make_public']) && $_POST['make_public'] === 'true';
    
    if (!$post_id) {
        wp_send_json_error(array(
            DGA_MESSAGE_KEY => __('ID ‡πÇ‡∏û‡∏™‡∏ï‡πå‡πÑ‡∏°‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á', DGA_TEXT_DOMAIN)
        ), 400);
    }
    
    // Get post
    $post = get_post($post_id);
    if (!$post) {
        wp_send_json_error(array(
            DGA_MESSAGE_KEY => __('‡πÑ‡∏°‡πà‡∏û‡∏ö‡πÇ‡∏û‡∏™‡∏ï‡πå', DGA_TEXT_DOMAIN)
        ), 404);
    }
    
    // Check permissions
    $current_user_id = get_current_user_id();
    $can_edit = false;
    
    if ($current_user_id == $post->post_author) {
        $can_edit = current_user_can('edit_post', $post_id);
    } elseif (current_user_can('edit_others_posts')) {
        $can_edit = true;
    }
    
    if (!$can_edit) {
        wp_send_json_error(array(
            DGA_MESSAGE_KEY => __('‡∏Ñ‡∏∏‡∏ì‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‡πÇ‡∏û‡∏™‡∏ï‡πå‡∏ô‡∏µ‡πâ', DGA_TEXT_DOMAIN)
        ), 403);
    }
    
    $success = true;
    $errors = array();
    
    // Update based on public/private state
    if ($make_public) {
        // Make public - publish and set as public data
        
        // Update taxonomy to ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏™‡∏≤‡∏ò‡∏≤‡∏£‡∏ì‡∏∞
        $term_result = wp_set_object_terms($post_id, '‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏™‡∏≤‡∏ò‡∏≤‡∏£‡∏ì‡∏∞', 'cgov', false);
        if (is_wp_error($term_result)) {
            $errors[] = $term_result->get_error_message();
            $success = false;
        }
        
        // Update post status to publish
        $status_result = wp_update_post(array(
            'ID' => $post_id,
            DGA_POST_STATUS_FIELD => DGA_PUBLISH_STATUS
        ), true);
        
        if (is_wp_error($status_result)) {
            $errors[] = $status_result->get_error_message();
            $success = false;
        }
        
        $new_status = DGA_PUBLISH_STATUS;
        $message = __('‡πÇ‡∏û‡∏™‡∏ï‡πå‡πÄ‡∏ú‡∏¢‡πÅ‡∏û‡∏£‡πà‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢‡πÅ‡∏•‡πâ‡∏ß', DGA_TEXT_DOMAIN);
        
    } else {
        // Make private - set to pending and mark as secret
        
        // Update taxonomy to ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏•‡∏±‡∏ö
        $term_result = wp_set_object_terms($post_id, '‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏•‡∏±‡∏ö', 'cgov', false);
        if (is_wp_error($term_result)) {
            $errors[] = $term_result->get_error_message();
            $success = false;
        }
        
        // Update post status to pending
        $status_result = wp_update_post(array(
            'ID' => $post_id,
            DGA_POST_STATUS_FIELD => 'pending'
        ), true);
        
        if (is_wp_error($status_result)) {
            $errors[] = $status_result->get_error_message();
            $success = false;
        }
        
        $new_status = 'pending';
        $message = __('‡∏ã‡πà‡∏≠‡∏ô‡πÇ‡∏û‡∏™‡∏ï‡πå‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢‡πÅ‡∏•‡πâ‡∏ß', DGA_TEXT_DOMAIN);
    }
    
    // Clear caches
    clean_post_cache($post_id);
    
    // Get updated post info
    $updated_post = get_post($post_id);
    $updated_terms = wp_get_post_terms($post_id, 'cgov', array(DGA_FIELDS_PARAMETER => 'names'));
    
    if ($success) {
        wp_send_json_success(array(
            DGA_MESSAGE_KEY => $message,
            DGA_STATUS_FIELD => $new_status,
            'make_public' => $make_public,
            DGA_POST_STATUS_FIELD => $updated_post->post_status,
            'terms' => $updated_terms
        ));
    } else {
        wp_send_json_error(array(
            DGA_MESSAGE_KEY => __('‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï', DGA_TEXT_DOMAIN),
            'errors' => $errors
        ));
    }
    
    die();
}

