/**
 * Convert Gregorian year to Buddhist year (‡∏û.‡∏®.)
 */
function std_convert_to_buddhist_year($gregorian_date) {
    $date_obj = date_create($gregorian_date);
    if (!$date_obj) {
        return $gregorian_date;
    }
    
    $year = intval(date_format($date_obj, 'Y')) + 543;
    $month_day = date_format($date_obj, 'd M');
    
    return $month_day . ' ' . $year;
}

/**
 * Get available years for filtering (2560-2568 ‡∏û.‡∏®.)
 */
function std_get_filter_years() {
    $years = array();
    for ($i = 2017; $i <= 2025; $i++) {
        $buddhist_year = $i + 543;
        $years[$i] = $buddhist_year;
    }
    return $years;
}

/**
 * Generate accessible badge HTML
 * @param string $type Badge type (mrdh/msprr)
 * @param string $value Document number value
 * @return string HTML for badge
 */
function std_generate_accessible_badge($type, $value) {
    if (empty($value)) {
        return '';
    }
    
    $badge_text = '';
    $aria_label = '';
    
    switch ($type) {
        case 'mrdh':
            $badge_text = sprintf(__('‡πÄ‡∏•‡∏Ç‡∏ó‡∏µ‡πà ‡∏°‡∏£‡∏î. %s', DGA_TEXT_DOMAIN), esc_html($value));
            $aria_label = sprintf(__('‡∏´‡∏°‡∏≤‡∏¢‡πÄ‡∏•‡∏Ç‡πÄ‡∏≠‡∏Å‡∏™‡∏≤‡∏£‡∏°‡∏≤‡∏ï‡∏£‡∏ê‡∏≤‡∏ô ‡∏°‡∏£‡∏î. %s', DGA_TEXT_DOMAIN), esc_html($value));
            break;
        case 'msprr':
            $badge_text = sprintf(__('‡πÄ‡∏•‡∏Ç‡∏ó‡∏µ‡πà ‡∏°‡∏™‡∏û‡∏£. %s', DGA_TEXT_DOMAIN), esc_html($value));
            $aria_label = sprintf(__('‡∏´‡∏°‡∏≤‡∏¢‡πÄ‡∏•‡∏Ç‡πÄ‡∏≠‡∏Å‡∏™‡∏≤‡∏£‡∏°‡∏≤‡∏ï‡∏£‡∏ê‡∏≤‡∏ô ‡∏°‡∏™‡∏û‡∏£. %s', DGA_TEXT_DOMAIN), esc_html($value));
            break;
        default:
            return '';
    }
    
    return sprintf(
        '<span class="doc-number %s" role="text" aria-label="%s">%s</span>',
        esc_attr($type),
        esc_attr($aria_label),
        $badge_text
    );
}

