/**
 * AJAX Handler ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏ó‡∏î‡∏™‡∏≠‡∏ö API endpoint
 */
function dga_test_api_endpoint_ajax_xy34() {
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö nonce ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏°‡∏õ‡∏•‡∏≠‡∏î‡∏†‡∏±‡∏¢
    if (!isset($_POST['nonce']) || !wp_verify_nonce($_POST['nonce'], 'dga_endpoint_test_nonce_xy34')) {
        wp_send_json_error(array(
            DGA_MESSAGE_KEY => __('‡∏Å‡∏≤‡∏£‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ñ‡∏ß‡∏≤‡∏°‡∏õ‡∏•‡∏≠‡∏î‡∏†‡∏±‡∏¢‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß', DGA_TEXT_DOMAIN)
        ));
    }
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Å‡∏≤‡∏£‡∏•‡πá‡∏≠‡∏Å‡∏≠‡∏¥‡∏ô
    if (!is_user_logged_in()) {
        wp_send_json_error(array(
            DGA_MESSAGE_KEY => __('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡πÄ‡∏Ç‡πâ‡∏≤‡∏™‡∏π‡πà‡∏£‡∏∞‡∏ö‡∏ö‡∏Å‡πà‡∏≠‡∏ô‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô', DGA_TEXT_DOMAIN)
        ));
    }
    
    // ‡∏£‡∏±‡∏ö‡πÅ‡∏•‡∏∞‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏• input
    $endpoint_url = isset($_POST['endpoint']) ? esc_url_raw($_POST['endpoint']) : '';
    $http_method = isset($_POST['method']) ? sanitize_text_field($_POST['method']) : 'GET';
    $custom_headers = isset($_POST['headers']) ? sanitize_textarea_field($_POST['headers']) : '';
    $post_data = isset($_POST['post_data']) ? sanitize_textarea_field($_POST['post_data']) : '';
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö URL
    if (empty($endpoint_url)) {
        wp_send_json_error(array(
            DGA_MESSAGE_KEY => __('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏£‡∏∞‡∏ö‡∏∏ URL Endpoint', DGA_TEXT_DOMAIN)
        ));
    }
    
    // ‡∏î‡∏∂‡∏á API key ‡∏Ç‡∏≠‡∏á‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ
    $current_user_id = get_current_user_id();
    $api_key = get_user_meta($current_user_id, 'api_key', true);
    
    if (empty($api_key)) {
        wp_send_json_error(array(
            DGA_MESSAGE_KEY => __('‡πÑ‡∏°‡πà‡∏û‡∏ö API KEY ‡πÇ‡∏õ‡∏£‡∏î‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤ API KEY ‡∏Å‡πà‡∏≠‡∏ô‡∏ó‡∏î‡∏™‡∏≠‡∏ö', DGA_TEXT_DOMAIN)
        ));
    }
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á headers ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö API request (‡∏à‡∏≥‡∏•‡∏≠‡∏á browser)
    $headers = array(
        'api-key' => trim($api_key),
        'User-Agent' => 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36',
        'Accept' => 'application/json, text/plain, */*',
        'Accept-Language' => 'en-US,en;q=0.9,th;q=0.8',
        'Accept-Encoding' => 'gzip, deflate, br',
        'Connection' => 'keep-alive',
        'Upgrade-Insecure-Requests' => '1',
        'Sec-Fetch-Dest' => 'document',
        'Sec-Fetch-Mode' => 'navigate',
        'Sec-Fetch-Site' => 'none',
        'Sec-Fetch-User' => '?1',
        'Cache-Control' => 'max-age=0'
    );
    
    // ‡πÄ‡∏û‡∏¥‡πà‡∏° custom headers ‡∏ñ‡πâ‡∏≤‡∏°‡∏µ
    if (!empty($custom_headers)) {
        $decoded_headers = json_decode($custom_headers, true);
        if (json_last_error() === JSON_ERROR_NONE && is_array($decoded_headers)) {
            foreach ($decoded_headers as $key => $value) {
                // ‡πÑ‡∏°‡πà‡πÉ‡∏´‡πâ‡πÅ‡∏ó‡∏ô‡∏ó‡∏µ‡πà api-key ‡∏ó‡∏µ‡πà‡∏°‡∏µ‡∏≠‡∏¢‡∏π‡πà‡πÅ‡∏•‡πâ‡∏ß
                if (strtolower($key) !== 'api-key') {
                    $headers[sanitize_text_field($key)] = sanitize_text_field($value);
                }
            }
        } else {
            wp_send_json_error(array(
                DGA_MESSAGE_KEY => __('‡∏£‡∏π‡∏õ‡πÅ‡∏ö‡∏ö Headers ‡πÑ‡∏°‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á (‡∏ï‡πâ‡∏≠‡∏á‡πÄ‡∏õ‡πá‡∏ô JSON)', DGA_TEXT_DOMAIN)
            ));
        }
    }
    
    // ‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤‡∏û‡∏≤‡∏£‡∏≤‡∏°‡∏¥‡πÄ‡∏ï‡∏≠‡∏£‡πå‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö HTTP request (‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏Å‡∏≤‡∏£‡∏õ‡πâ‡∏≠‡∏á‡∏Å‡∏±‡∏ô‡∏Å‡∏≤‡∏£‡∏ñ‡∏π‡∏Å‡∏ö‡∏•‡πá‡∏≠‡∏Ñ)
    $request_args = array(
        'timeout' => 30,
        'headers' => $headers,
        'sslverify' => true,
        'user-agent' => 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36',
        'httpversion' => '1.1',
        'redirection' => 5,
        'blocking' => true,
        'cookies' => array(),
        'decompress' => true
    );
    
    // ‡πÄ‡∏û‡∏¥‡πà‡∏° delay ‡πÄ‡∏•‡πá‡∏Å‡∏ô‡πâ‡∏≠‡∏¢‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏´‡∏•‡∏µ‡∏Å‡πÄ‡∏•‡∏µ‡πà‡∏¢‡∏á rate limiting
    usleep(500000); // 0.5 ‡∏ß‡∏¥‡∏ô‡∏≤‡∏ó‡∏µ
    
    // ‡πÄ‡∏û‡∏¥‡πà‡∏° POST data ‡∏ñ‡πâ‡∏≤‡πÄ‡∏õ‡πá‡∏ô POST method
    if ($http_method === 'POST' && !empty($post_data)) {
        $decoded_data = json_decode($post_data, true);
        if (json_last_error() === JSON_ERROR_NONE) {
            $request_args['body'] = $post_data;
            if (!isset($headers['Content-Type'])) {
                $request_args['headers']['Content-Type'] = 'application/json';
            }
        } else {
            wp_send_json_error(array(
                DGA_MESSAGE_KEY => __('‡∏£‡∏π‡∏õ‡πÅ‡∏ö‡∏ö POST Data ‡πÑ‡∏°‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á (‡∏ï‡πâ‡∏≠‡∏á‡πÄ‡∏õ‡πá‡∏ô JSON)', DGA_TEXT_DOMAIN)
            ));
        }
    }
    
    // ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡πÄ‡∏ß‡∏•‡∏≤‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô
    $start_time = microtime(true);
    
    // ‡∏™‡πà‡∏á HTTP request
    if ($http_method === 'POST') {
        $response = wp_remote_post($endpoint_url, $request_args);
    } else {
        $response = wp_remote_get($endpoint_url, $request_args);
    }
    
    // ‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡πÄ‡∏ß‡∏•‡∏≤‡∏ï‡∏≠‡∏ö‡∏™‡∏ô‡∏≠‡∏á
    $response_time = round((microtime(true) - $start_time) * 1000);
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î
    if (is_wp_error($response)) {
        $error_message = $response->get_error_message();
        $error_code = $response->get_error_code();
        
        wp_send_json_error(array(
            DGA_MESSAGE_KEY => sprintf(__('‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î: %s', DGA_TEXT_DOMAIN), $error_message),
            'error_code' => $error_code,
            'response_time' => $response_time . ' ms'
        ));
    }
    
    // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏≤‡∏Å response
    $status_code = wp_remote_retrieve_response_code($response);
    $response_headers = wp_remote_retrieve_headers($response);
    $body = wp_remote_retrieve_body($response);
    
    // ‡πÅ‡∏õ‡∏•‡∏á JSON response
    $parsed_body = json_decode($body, true);
    if (json_last_error() !== JSON_ERROR_NONE) {
        $parsed_body = $body; // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡πÉ‡∏ä‡πà JSON ‡πÉ‡∏´‡πâ‡πÉ‡∏ä‡πâ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏î‡∏¥‡∏ö
    }
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ï‡∏±‡∏ß‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÅ‡∏™‡∏î‡∏á‡∏ú‡∏•
    $preview_data = dga_extract_preview_data_xy34($parsed_body);
    
    // ‡∏™‡πà‡∏á‡∏ú‡∏•‡∏•‡∏±‡∏û‡∏ò‡πå‡∏Å‡∏•‡∏±‡∏ö
    wp_send_json_success(array(
        'status_code' => $status_code,
        'response_time' => $response_time . ' ms',
        'headers' => $response_headers,
        'response' => $parsed_body,
        'preview_data' => $preview_data,
        'request_info' => array(
            'url' => $endpoint_url,
            'method' => $http_method,
            'headers_sent' => $headers,
            'api_key_used' => substr($api_key, 0, 4) . '...' . substr($api_key, -4),
            'api_key_length' => strlen($api_key),
            'custom_headers_count' => !empty($custom_headers) ? count(json_decode($custom_headers, true) ?: array()) : 0,
            DGA_SUCCESS_STATUS => true
        ),
        'debug_info' => array(
            'api_key_exists' => !empty($api_key),
            'api_key_preview' => substr($api_key, 0, 4) . '***' . substr($api_key, -4),
            'headers_count' => count($headers),
            'wp_version' => get_bloginfo('version'),
            'user_id' => $current_user_id
        )
    ));
}
