/**
 * ‡πÅ‡∏¢‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÅ‡∏™‡∏î‡∏á‡∏ï‡∏±‡∏ß‡∏≠‡∏¢‡πà‡∏≤‡∏á
 */
function dga_extract_preview_data_xy34($response) {
    if (empty($response) || !is_array($response)) {
        return array(
            DGA_TYPE_FIELD => DGA_ERROR_STATUS,
            DGA_MESSAGE_KEY => __('‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡πÅ‡∏™‡∏î‡∏á‡∏ï‡∏±‡∏ß‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÑ‡∏î‡πâ', DGA_TEXT_DOMAIN)
        );
    }
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏£‡∏π‡∏õ‡πÅ‡∏ö‡∏ö data.go.th standard
    if (isset($response['result'])) {
        $result = $response['result'];
        
        // ‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡πÅ‡∏ö‡∏ö‡∏á‡πà‡∏≤‡∏¢ (package_list, group_list)
        if (is_array($result) && !empty($result) && is_string($result[0])) {
            return array(
                DGA_TYPE_FIELD => 'list',
                'data' => array_slice($result, 0, 20), // ‡πÅ‡∏™‡∏î‡∏á‡πÅ‡∏Ñ‡πà 20 ‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡πÅ‡∏£‡∏Å
                DGA_TOTAL_FIELD_KEY => count($result),
                DGA_TITLE_FIELD => __('‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•', DGA_TEXT_DOMAIN)
            );
        }
        
        // ‡∏ú‡∏•‡∏Å‡∏≤‡∏£‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤ (search results)
        if (isset($result['results']) && is_array($result['results'])) {
            return array(
                DGA_TYPE_FIELD => 'table',
                'data' => array_slice($result['results'], 0, 10),
                DGA_TOTAL_FIELD_KEY => isset($result['count']) ? $result['count'] : count($result['results']),
                DGA_TITLE_FIELD => __('‡∏ú‡∏•‡∏Å‡∏≤‡∏£‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤', DGA_TEXT_DOMAIN)
            );
        }
        
        // ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏ô‡∏£‡∏π‡∏õ‡πÅ‡∏ö‡∏ö object array
        if (is_array($result) && !empty($result) && is_array($result[0])) {
            return array(
                DGA_TYPE_FIELD => 'table',
                'data' => array_slice($result, 0, 10),
                DGA_TOTAL_FIELD_KEY => count($result),
                DGA_TITLE_FIELD => __('‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏≤‡∏Å API', DGA_TEXT_DOMAIN)
            );
        }
    }
    
    // ‡∏£‡∏π‡∏õ‡πÅ‡∏ö‡∏ö API ‡∏≠‡∏∑‡πà‡∏ô‡πÜ
    if (isset($response['data']) && is_array($response['data'])) {
        return array(
            DGA_TYPE_FIELD => 'table',
            'data' => array_slice($response['data'], 0, 10),
            DGA_TOTAL_FIELD_KEY => isset($response[DGA_TOTAL_FIELD_KEY]) ? $response[DGA_TOTAL_FIELD_KEY] : count($response['data']),
            DGA_TITLE_FIELD => __('‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏≤‡∏Å API', DGA_TEXT_DOMAIN)
        );
    }
    
    // Fallback: ‡πÅ‡∏™‡∏î‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏î‡∏¥‡∏ö
    return array(
        DGA_TYPE_FIELD => 'raw',
        'data' => $response,
        DGA_TITLE_FIELD => __('‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏î‡∏¥‡∏ö‡∏à‡∏≤‡∏Å API', DGA_TEXT_DOMAIN)
    );
}

