/**
 * ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÄ‡∏£‡∏µ‡∏¢‡∏Å API ‡πÇ‡∏î‡∏¢‡∏ï‡∏£‡∏á
 */
function dga_try_direct_api_call_xy34($endpoint_url, $headers, $method = 'GET', $body = null) {
    $args = array(
        'timeout' => 30,
        'headers' => $headers,
        'sslverify' => true,
        'user-agent' => 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36',
        'httpversion' => '1.1'
    );
    
    if ($method === 'POST' && $body) {
        $args['body'] = $body;
        if (!isset($headers['Content-Type'])) {
            $args['headers']['Content-Type'] = 'application/json';
        }
    }
    
    // ‡πÄ‡∏û‡∏¥‡πà‡∏° delay ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏´‡∏•‡∏µ‡∏Å‡πÄ‡∏•‡∏µ‡πà‡∏¢‡∏á rate limiting
    usleep(500000); // 0.5 ‡∏ß‡∏¥‡∏ô‡∏≤‡∏ó‡∏µ
    
    if ($method === 'POST') {
        return wp_remote_post($endpoint_url, $args);
    } else {
        return wp_remote_get($endpoint_url, $args);
    }
}

