/**
 * AJAX handler for user creation
 */
function dga_ajax_create_user_hjk456() {
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö nonce
    if (!check_ajax_referer('dga_create_user_nonce_hjk456', 'nonce', false)) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏Å‡∏≤‡∏£‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ñ‡∏ß‡∏≤‡∏°‡∏õ‡∏•‡∏≠‡∏î‡∏†‡∏±‡∏¢‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß', DGA_TEXT_DOMAIN)]);
    }
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå
    if (!current_user_can('create_users')) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏Ñ‡∏∏‡∏ì‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ', DGA_TEXT_DOMAIN)]);
    }
    
    // ‡∏£‡∏±‡∏ö‡πÅ‡∏•‡∏∞‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•
    $email = sanitize_email($_POST['email'] ?? '');
    $role = sanitize_text_field($_POST['role'] ?? 'subscriber');
    
    // Validate email
    if (!is_email($email)) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡πÉ‡∏´‡πâ‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á', DGA_TEXT_DOMAIN)]);
    }
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏°‡∏µ‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡∏≠‡∏¢‡∏π‡πà‡πÅ‡∏•‡πâ‡∏ß‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
    if (email_exists($email)) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡∏ô‡∏µ‡πâ‡∏°‡∏µ‡∏≠‡∏¢‡∏π‡πà‡πÉ‡∏ô‡∏£‡∏∞‡∏ö‡∏ö‡πÅ‡∏•‡πâ‡∏ß', DGA_TEXT_DOMAIN)]);
    }
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ
    $username = $email;
    $password = wp_generate_password(16, true, true);
    $user_id = wp_create_user($username, $password, $email);
    
    if (is_wp_error($user_id)) {
        wp_send_json_error([DGA_MESSAGE_KEY => $user_id->get_error_message()]);
    }
    
    // ‡∏Å‡∏≥‡∏´‡∏ô‡∏î role
    $user = new WP_User($user_id);
    $user->set_role($role);
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á reset key
    $reset_key = dga_generate_password_reset_key_hjk456($user_id);
    
    if (!$reset_key) {
        // ‡∏•‡∏ö user ‡∏ó‡∏µ‡πà‡∏™‡∏£‡πâ‡∏≤‡∏á‡πÑ‡∏ß‡πâ
        wp_delete_user($user_id);
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏•‡∏¥‡∏á‡∏Å‡πå‡∏£‡∏µ‡πÄ‡∏ã‡πá‡∏ï‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÑ‡∏î‡πâ', DGA_TEXT_DOMAIN)]);
    }
    
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏•‡∏¥‡∏á‡∏Å‡πå
    $reset_link = add_query_arg([
        'action' => 'dga_set_password',
        'key' => $reset_key,
        'login' => rawurlencode($username)
    ], home_url());
    
    // ‡∏™‡πà‡∏á‡∏≠‡∏µ‡πÄ‡∏°‡∏•
    $email_sent = dga_send_welcome_email_hjk456($email, $reset_link, $role);
    
    if ($email_sent) {
        wp_send_json_success([
            DGA_MESSAGE_KEY => __('‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à! ‡∏£‡∏∞‡∏ö‡∏ö‡πÑ‡∏î‡πâ‡∏™‡πà‡∏á‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡πÑ‡∏õ‡∏¢‡∏±‡∏á‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡πÅ‡∏•‡πâ‡∏ß', DGA_TEXT_DOMAIN),
            'user_id' => $user_id,
            'email' => $email
        ]);
    } else {
        wp_send_json_success([
            DGA_MESSAGE_KEY => __('‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à ‡πÅ‡∏ï‡πà‡∏Å‡∏≤‡∏£‡∏™‡πà‡∏á‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß', DGA_TEXT_DOMAIN),
            'user_id' => $user_id,
            'email' => $email,
            'warning' => true
        ]);
    }
}
