/**
 * AJAX handler for password reset
 */
function dga_ajax_reset_password_hjk456() {
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö nonce
    if (!check_ajax_referer('dga_reset_password_hjk456', 'nonce', false)) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏Å‡∏≤‡∏£‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ñ‡∏ß‡∏≤‡∏°‡∏õ‡∏•‡∏≠‡∏î‡∏†‡∏±‡∏¢‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß', DGA_TEXT_DOMAIN)]);
    }
    
    $pass1 = $_POST['pass1'] ?? '';
    $pass2 = $_POST['pass2'] ?? '';
    $key = sanitize_text_field($_POST['key'] ?? '');
    $login = sanitize_user($_POST['login'] ?? '');
    $user_id = intval($_POST['user_id'] ?? 0);
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô
    if (empty($pass1) || empty($pass2)) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô', DGA_TEXT_DOMAIN)]);
    }
    
    if ($pass1 !== $pass2) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÑ‡∏°‡πà‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ô', DGA_TEXT_DOMAIN)]);
    }
    
    if (strlen($pass1) < 8) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡∏ï‡πâ‡∏≠‡∏á‡∏°‡∏µ‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏ô‡πâ‡∏≠‡∏¢ 8 ‡∏ï‡∏±‡∏ß‡∏≠‡∏±‡∏Å‡∏©‡∏£', DGA_TEXT_DOMAIN)]);
    }
    
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö key ‡∏≠‡∏µ‡∏Å‡∏Ñ‡∏£‡∏±‡πâ‡∏á
    $user = check_password_reset_key($key, $login);
    
    if (is_wp_error($user) || $user->ID !== $user_id) {
        wp_send_json_error([DGA_MESSAGE_KEY => __('‡∏•‡∏¥‡∏á‡∏Å‡πå‡πÑ‡∏°‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á‡∏´‡∏£‡∏∑‡∏≠‡∏´‡∏°‡∏î‡∏≠‡∏≤‡∏¢‡∏∏', DGA_TEXT_DOMAIN)]);
    }
    
    // ‡∏ï‡∏±‡πâ‡∏á‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÉ‡∏´‡∏°‡πà
    wp_set_password($pass1, $user->ID);
    
    // ‡∏•‡πá‡∏≠‡∏Å‡∏≠‡∏¥‡∏ô‡∏≠‡∏±‡∏ï‡πÇ‡∏ô‡∏°‡∏±‡∏ï‡∏¥
    wp_clear_auth_cookie();
    wp_set_current_user($user->ID);
    wp_set_auth_cookie($user->ID, true);
    
    // ‡∏•‡∏ö meta ‡∏ó‡∏µ‡πà‡πÄ‡∏Å‡∏µ‡πà‡∏¢‡∏ß‡∏Ç‡πâ‡∏≠‡∏á
    delete_user_meta($user->ID, 'dga_reset_requested_hjk456');
    
    wp_send_json_success([
        DGA_MESSAGE_KEY => __('‡∏ï‡∏±‡πâ‡∏á‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à!', DGA_TEXT_DOMAIN),
        'redirect' => home_url()
    ]);
}
add_action('wp_ajax_nopriv_dga_reset_password', 'dga_ajax_reset_password_hjk456');

