if (!function_exists('dga_thai_date_zxk429')) {
    function dga_thai_date_zxk429($atts) {
        // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏≠‡∏¢‡∏π‡πà‡πÉ‡∏ô‡∏´‡∏ô‡πâ‡∏≤ single post ‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
        if (!is_single()) {
