        ob_start();
        ?>
        <div class="dga-container-zxk429" data-postid="<?php echo esc_attr($post_id); ?>" role="group" aria-label="<?php _e('‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÇ‡∏û‡∏™‡∏ï‡πå', DGA_TEXT_DOMAIN); ?>">
            <span class="dga-date-zxk429" aria-label="<?php _e('‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà‡πÄ‡∏ú‡∏¢‡πÅ‡∏û‡∏£‡πà', DGA_TEXT_DOMAIN); ?>">
