                $response_data = $new_field;
            } else {
                $key_to_update = isset($_POST['key']) ? sanitize_text_field($_POST['key']) : '';
                $field_found = false;
                
                foreach ($field_group[DGA_FIELDS_PARAMETER] as &$field) {
