            }

            acf_import_field_group($field_group);
            wp_send_json_success([DGA_MESSAGE_KEY => $message, DGA_FIELD_KEY => $response_data]);
            break;

        case 'delete':
            $key_to_delete = isset($_POST['key']) ? sanitize_text_field($_POST['key']) : '';
            
            if (empty($key_to_delete)) {
